{
  "name": "Branch Performance Calculator",
  "description": "Nástroj pro analýzu excelových dat pobočky Budějovická s automatickým výpočtem denního cíle na základě zbývajících dnů v měsíci."
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export interface ExcelRow {
  BranchName: string;
  'Revenue RR'?: number | string;
  'AR Asist Service Revenue'?: number | string;
  'Service Asist Revenue'?: number | string;
  [key: string]: any;
}

export interface CalculationResult {
  branchName: string;
  revenueRR: number;
  arAsistServiceRevenue: number;
  serviceAsistRevenue: number;
  daysRemaining: number;
  finalValue: number;
  rawRow: ExcelRow;
}

export const getDaysRemainingInMonth = (): number => {
  const now = new Date();
  const year = now.getFullYear();
  const month = now.getMonth();

  // Last day of current month
  const lastDay = new Date(year, month + 1, 0).getDate();
  const currentDay = now.getDate();

  // Days remaining including today would be (lastDay - currentDay + 1)
  // Usually "remaining days" refers to the time after today, but in sales logic it often includes today.
  // We will return (lastDay - currentDay) as "days after today". 
  // If currentDay is lastDay, it returns 0. To avoid division by zero, we return at least 1.
  const remaining = lastDay - currentDay;
  return remaining > 0 ? remaining : 1;
};

export const formatDate = (date: Date): string => {
  return new Intl.DateTimeFormat('cs-CZ', {
    day: 'numeric',
    month: 'long',
    year: 'numeric'
  }).format(date);
};

import React, { useState, useCallback } from 'react';
import * as XLSX from 'xlsx';
import { Upload, FileSpreadsheet, Calculator, AlertCircle, CheckCircle2, Info, ArrowRight } from 'lucide-react';
import { ExcelRow, CalculationResult } from './types';
import { getDaysRemainingInMonth, formatDate } from './utils/dateUtils';

const App: React.FC = () => {
  const [data, setData] = useState<CalculationResult[]>([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [fileName, setFileName] = useState<string | null>(null);

  const processFile = async (file: File) => {
    setIsProcessing(true);
    setError(null);
    setFileName(file.name);

    try {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const ab = e.target?.result;
          const wb = XLSX.read(ab, { type: 'array' });
          const wsname = wb.SheetNames[0];
          const ws = wb.Sheets[wsname];
          const json = XLSX.utils.sheet_to_json<ExcelRow>(ws);

          const daysRemaining = getDaysRemainingInMonth();
          const targetBranch = "Budějovická";

          const results: CalculationResult[] = json
            .filter(row => row.BranchName?.toString().trim() === targetBranch)
            .map(row => {
              const revRR = parseFloat(String(row['Revenue RR'] || 0));
              const arAsist = parseFloat(String(row['AR Asist Service Revenue'] || 0));
              const serAsist = parseFloat(String(row['Service Asist Revenue'] || 0));

              // Formula: (Revenue RR * AR Asist Service Revenue - Service Asist Revenue) / Days Remaining
              const finalValue = (revRR * arAsist - serAsist) / daysRemaining;

              return {
                branchName: targetBranch,
                revenueRR: revRR,
                arAsistServiceRevenue: arAsist,
                serviceAsistRevenue: serAsist,
                daysRemaining,
                finalValue,
                rawRow: row
              };
            });

          if (results.length === 0) {
            setError(`V souboru nebyla nalezena pobočka "${targetBranch}". Zkontrolujte prosím název sloupce "BranchName".`);
          }

          setData(results);
        } catch (err) {
          setError("Chyba při zpracování obsahu Excelu. Ujistěte se, že soubor má správnou strukturu.");
        } finally {
          setIsProcessing(false);
        }
      };
      reader.readAsArrayBuffer(file);
    } catch (err) {
      setError("Nepodařilo se načíst soubor.");
      setIsProcessing(false);
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      processFile(e.target.files[0]);
    }
  };

  return (
    <div className="max-w-5xl mx-auto px-4 py-12">
      {/* Header */}
      <header className="mb-12 text-center">
        <div className="inline-flex items-center justify-center p-3 bg-blue-600 rounded-2xl mb-4 shadow-lg shadow-blue-200">
          <Calculator className="w-8 h-8 text-white" />
        </div>
        <h1 className="text-4xl font-extrabold text-slate-900 mb-2">Analyzátor Pobočky</h1>
        <p className="text-slate-500 text-lg max-w-2xl mx-auto">
          Nahrajte měsíční report a okamžitě získejte propočty pro pobočku <span className="text-blue-600 font-semibold">Budějovická</span>.
        </p>
      </header>

      {/* Main Content */}
      <main className="space-y-8">
        {/* Upload Zone */}
        <div className={`relative group border-2 border-dashed rounded-3xl p-12 transition-all duration-300 text-center
          ${fileName ? 'border-green-300 bg-green-50' : 'border-slate-300 bg-white hover:border-blue-400 hover:bg-blue-50/30'}`}>
          <input
            type="file"
            accept=".xlsx, .xls"
            onChange={handleFileChange}
            className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
          />
          <div className="flex flex-col items-center gap-4">
            <div className={`p-4 rounded-full transition-transform duration-300 group-hover:scale-110 
              ${fileName ? 'bg-green-100 text-green-600' : 'bg-blue-100 text-blue-600'}`}>
              {fileName ? <CheckCircle2 className="w-10 h-10" /> : <Upload className="w-10 h-10" />}
            </div>
            <div>
              <p className="text-xl font-bold text-slate-800">
                {fileName ? fileName : 'Klikněte nebo přetáhněte Excel soubor'}
              </p>
              <p className="text-slate-400 mt-1">Podporuje .xlsx a .xls formáty</p>
            </div>
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="flex items-center gap-3 p-4 bg-red-50 border border-red-200 text-red-700 rounded-2xl animate-in fade-in slide-in-from-top-2">
            <AlertCircle className="w-5 h-5 flex-shrink-0" />
            <p className="font-medium">{error}</p>
          </div>
        )}

        {/* Processing Loader */}
        {isProcessing && (
          <div className="flex justify-center py-10">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
          </div>
        )}

        {/* Results */}
        {data.length > 0 && !isProcessing && (
          <div className="grid gap-6 animate-in fade-in zoom-in-95 duration-500">
            <div className="flex items-center justify-between px-2">
              <h2 className="text-2xl font-bold text-slate-800 flex items-center gap-2">
                <FileSpreadsheet className="text-blue-500" />
                Výsledky pro pobočku Budějovická
              </h2>
              <span className="px-4 py-1.5 bg-slate-200 text-slate-700 rounded-full text-sm font-semibold">
                Dnes: {formatDate(new Date())}
              </span>
            </div>

            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
              {data.map((result, idx) => (
                <div key={idx} className="bg-white rounded-3xl shadow-sm border border-slate-100 overflow-hidden group hover:shadow-xl transition-shadow duration-300">
                  <div className="bg-blue-600 p-5 text-white">
                    <div className="flex justify-between items-start mb-4">
                      <p className="text-blue-100 text-sm font-medium uppercase tracking-wider">Výsledná hodnota</p>
                      <Info className="w-4 h-4 text-blue-200 cursor-help" title="Denní cíl do konce měsíce" />
                    </div>
                    <div className="flex items-baseline gap-1">
                      <h3 className="text-4xl font-black">
                        {result.finalValue.toLocaleString('cs-CZ', { maximumFractionDigits: 2 })}
                      </h3>
                      <span className="text-blue-200 font-medium">CZK / den</span>
                    </div>
                  </div>

                  <div className="p-6 space-y-4">
                    <div className="flex justify-between items-center py-2 border-b border-slate-50">
                      <span className="text-slate-500 text-sm">Revenue RR</span>
                      <span className="font-semibold text-slate-800">{result.revenueRR.toLocaleString()}</span>
                    </div>
                    <div className="flex justify-between items-center py-2 border-b border-slate-50">
                      <span className="text-slate-500 text-sm italic">AR Asist Serv. Rev.</span>
                      <span className="font-semibold text-slate-800">{result.arAsistServiceRevenue.toLocaleString()}</span>
                    </div>
                    <div className="flex justify-between items-center py-2 border-b border-slate-50">
                      <span className="text-slate-500 text-sm">Service Asist Rev.</span>
                      <span className="font-semibold text-slate-800">{result.serviceAsistRevenue.toLocaleString()}</span>
                    </div>
                    <div className="flex justify-between items-center py-2">
                      <span className="text-slate-500 text-sm font-medium">Zbývající dny</span>
                      <span className="px-3 py-1 bg-amber-100 text-amber-700 rounded-lg font-bold">
                        {result.daysRemaining}
                      </span>
                    </div>

                    <div className="mt-6 p-4 bg-slate-50 rounded-2xl text-xs text-slate-400 font-mono">
                      <p className="mb-1 uppercase tracking-tight font-bold text-slate-500">Logika výpočtu:</p>
                      <code>({result.revenueRR} * {result.arAsistServiceRevenue} - {result.serviceAsistRevenue}) / {result.daysRemaining}</code>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {/* Formula Explanation */}
            <div className="bg-blue-50 border border-blue-100 rounded-3xl p-8">
              <div className="flex items-start gap-4">
                <div className="p-3 bg-blue-100 rounded-2xl">
                  <Calculator className="text-blue-600 w-6 h-6" />
                </div>
                <div>
                  <h4 className="text-lg font-bold text-blue-900 mb-2">Jak počítáme výsledek?</h4>
                  <p className="text-blue-800/80 leading-relaxed max-w-3xl mb-4">
                    Aplikace automaticky filtruje data pro pobočku <strong>Budějovická</strong>. 
                    Následně vynásobíme hodnotu <strong>Revenue RR</strong> s <strong>AR Asist Service Revenue</strong>, 
                    odečteme <strong>Service Asist Revenue</strong> a zbytek rovnoměrně rozdělíme mezi {getDaysRemainingInMonth()} zbývajících dnů do konce měsíce.
                  </p>
                  <div className="flex flex-wrap items-center gap-3 text-sm font-bold text-blue-700">
                    <span className="px-3 py-1 bg-white rounded-lg border border-blue-200">Revenue RR</span>
                    <span className="text-blue-300">×</span>
                    <span className="px-3 py-1 bg-white rounded-lg border border-blue-200">AR Asist</span>
                    <span className="text-blue-300">−</span>
                    <span className="px-3 py-1 bg-white rounded-lg border border-blue-200">Service Asist</span>
                    <span className="text-blue-300">÷</span>
                    <span className="px-3 py-1 bg-white rounded-lg border border-blue-200">Dny</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Placeholder when no data */}
        {data.length === 0 && !isProcessing && !error && (
          <div className="py-20 flex flex-col items-center justify-center opacity-40">
            <FileSpreadsheet className="w-16 h-16 text-slate-300 mb-4" />
            <p className="text-slate-400 font-medium italic">Zatím nebyly nahrány žádné údaje</p>
          </div>
        )}
      </main>

      <footer className="mt-20 pt-8 border-t border-slate-200 text-center text-slate-400 text-sm">
        <p>&copy; {new Date().getFullYear()} Analytický Nástroj pro Pobočku Budějovická</p>
      </footer>
    </div>
  );
};

export default App;

export const getDaysRemainingInMonth = (): number => {
  const now = new Date();
  const year = now.getFullYear();
  const month = now.getMonth();

  // Poslední den měsíce (např. 31)
  const lastDay = new Date(year, month + 1, 0).getDate();
  const currentDay = now.getDate();

  // Pokud je dnes 20. a měsíc má 30 dní, zbývá 11 dní (včetně dneška)
  const remaining = (lastDay - currentDay) + 1;

  return remaining > 0 ? remaining : 1;
};

export const formatDate = (date: Date): string => {
  return new Intl.DateTimeFormat('cs-CZ', {
    day: 'numeric',
    month: 'long',
    year: 'numeric'
  }).format(date);
};

import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { 
  Upload, 
  FileSpreadsheet, 
  Calculator, 
  AlertCircle, 
  CheckCircle2, 
  Info, 
  TrendingUp, 
  Calendar, 
  Search,
  ArrowRightLeft
} from 'lucide-react';
import { ExcelRow, CalculationResult } from './types';
import { getDaysRemainingInMonth, formatDate } from './utils/dateUtils';

const App: React.FC = () => {
  const [data, setData] = useState<CalculationResult[]>([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [fileName, setFileName] = useState<string | null>(null);
  const [availableBranches, setAvailableBranches] = useState<string[]>([]);

  const processFile = async (file: File) => {
    setIsProcessing(true);
    setError(null);
    setFileName(file.name);
    setData([]);
    setAvailableBranches([]);

    try {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const ab = e.target?.result;
          const wb = XLSX.read(ab, { type: 'array' });
          const wsname = wb.SheetNames[0];
          const ws = wb.Sheets[wsname];
          const json = XLSX.utils.sheet_to_json<ExcelRow>(ws);

          const daysRemaining = getDaysRemainingInMonth();
          const targetBranch = "Budějovická";

          // Získání unikátních názvů poboček pro případnou pomoc uživateli
          const branches = Array.from(new Set(json.map(row => row.BranchName?.toString().trim()).filter(Boolean)));
          setAvailableBranches(branches as string[]);

          const results: CalculationResult[] = json
            .filter(row => {
              const bName = row.BranchName?.toString().trim();
              return bName?.toLowerCase() === targetBranch.toLowerCase();
            })
            .map(row => {
              const revRR = parseFloat(String(row['Revenue RR'] || 0).replace(',', '.'));
              const arAsist = parseFloat(String(row['AR Asist Service Revenue'] || 0).replace(',', '.'));
              const serAsist = parseFloat(String(row['Service Asist Revenue'] || 0).replace(',', '.'));

              // Vzorec: (Revenue RR * AR Asist Service Revenue - Service Asist Revenue) / Dny
              const numerator = (revRR * arAsist) - serAsist;
              const finalValue = numerator / daysRemaining;

              return {
                branchName: targetBranch,
                revenueRR: revRR,
                arAsistServiceRevenue: arAsist,
                serviceAsistRevenue: serAsist,
                daysRemaining,
                finalValue,
                rawRow: row
              };
            });

          if (results.length === 0) {
            setError(`Pobočka "${targetBranch}" nebyla v souboru nalezena.`);
          }

          setData(results);
        } catch (err) {
          setError("Chyba při zpracování obsahu Excelu. Zkontrolujte názvy sloupců (BranchName, Revenue RR, atd.).");
        } finally {
          setIsProcessing(false);
        }
      };
      reader.readAsArrayBuffer(file);
    } catch (err) {
      setError("Nepodařilo se načíst soubor.");
      setIsProcessing(false);
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      processFile(e.target.files[0]);
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-12 font-sans text-slate-900">
      {/* Header Section */}
      <header className="mb-12 flex flex-col items-center">
        <div className="bg-gradient-to-tr from-blue-600 to-indigo-600 p-4 rounded-3xl shadow-2xl shadow-blue-200 mb-6 transform hover:rotate-3 transition-transform">
          <Calculator className="w-10 h-10 text-white" />
        </div>
        <h1 className="text-4xl font-black tracking-tight text-slate-900 mb-3 text-center">
          Analýza pobočky <span className="text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-indigo-600">Budějovická</span>
        </h1>
        <div className="flex items-center gap-4 text-slate-500 bg-white px-6 py-2 rounded-full border border-slate-100 shadow-sm">
          <Calendar className="w-4 h-4 text-blue-500" />
          <span className="text-sm font-medium">Zbývající dny v měsíci: <strong className="text-slate-800">{getDaysRemainingInMonth()}</strong></span>
        </div>
      </header>

      <main className="space-y-10">
        {/* Upload Area */}
        <section>
          <div className={`relative group border-4 border-dashed rounded-[2.5rem] p-16 transition-all duration-500 text-center
            ${fileName ? 'border-green-200 bg-green-50/30' : 'border-slate-200 bg-white hover:border-blue-400 hover:shadow-2xl hover:shadow-blue-50'}`}>
            <input
              type="file"
              accept=".xlsx, .xls"
              onChange={handleFileChange}
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
            />
            <div className="flex flex-col items-center gap-6">
              <div className={`p-6 rounded-full shadow-inner transition-all duration-500 group-hover:scale-110 
                ${fileName ? 'bg-green-100 text-green-600' : 'bg-blue-50 text-blue-500'}`}>
                {fileName ? <CheckCircle2 className="w-12 h-12" /> : <Upload className="w-12 h-12 animate-bounce-slow" />}
              </div>
              <div className="max-w-sm">
                <p className="text-2xl font-bold text-slate-800 mb-2">
                  {fileName ? fileName : 'Vyberte report pro analýzu'}
                </p>
                <p className="text-slate-400">Přetáhněte sem soubor ve formátu Excel (.xlsx)</p>
              </div>
            </div>
          </div>
        </section>

        {/* Error State with Debug Info */}
        {error && (
          <div className="animate-in fade-in slide-in-from-top-4 duration-500">
            <div className="bg-red-50 border-2 border-red-100 rounded-3xl p-8 flex flex-col gap-6">
              <div className="flex items-center gap-4 text-red-700">
                <AlertCircle className="w-8 h-8 flex-shrink-0" />
                <h3 className="text-xl font-bold">{error}</h3>
              </div>

              {availableBranches.length > 0 && (
                <div className="bg-white/60 rounded-2xl p-6">
                  <p className="text-sm font-bold text-slate-500 uppercase tracking-widest mb-4 flex items-center gap-2">
                    <Search className="w-4 h-4" /> Nalezené pobočky v souboru ({availableBranches.length}):
                  </p>
                  <div className="flex flex-wrap gap-2">
                    {availableBranches.slice(0, 15).map((b, i) => (
                      <span key={i} className="px-3 py-1.5 bg-slate-100 text-slate-600 rounded-xl text-xs font-medium border border-slate-200">
                        {b}
                      </span>
                    ))}
                    {availableBranches.length > 15 && <span className="text-slate-400 text-xs self-center ml-2">... a další</span>}
                  </div>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Loading Spinner */}
        {isProcessing && (
          <div className="flex flex-col items-center justify-center py-20 gap-4">
            <div className="w-16 h-16 border-4 border-blue-100 border-t-blue-600 rounded-full animate-spin"></div>
            <p className="text-slate-400 font-medium animate-pulse">Zpracovávám data...</p>
          </div>
        )}

        {/* Results Dashboard */}
        {data.length > 0 && !isProcessing && (
          <div className="animate-in zoom-in-95 fade-in duration-700 space-y-8">
            {data.map((result, idx) => (
              <div key={idx} className="bg-white rounded-[3rem] shadow-2xl shadow-slate-200/50 border border-slate-100 overflow-hidden">
                <div className="grid lg:grid-cols-5 h-full">

                  {/* Left Column: Result Card */}
                  <div className="lg:col-span-2 bg-gradient-to-br from-blue-700 to-indigo-800 p-10 text-white flex flex-col justify-between">
                    <div>
                      <div className="flex items-center gap-3 mb-8 opacity-80">
                        <TrendingUp className="w-5 h-5" />
                        <span className="text-sm font-bold uppercase tracking-widest">Denní výsledek</span>
                      </div>
                      <h2 className="text-7xl font-black mb-4 tracking-tighter">
                        {result.finalValue.toLocaleString('cs-CZ', { maximumFractionDigits: 0 })}
                      </h2>
                      <p className="text-blue-100 text-lg font-medium">CZK do konce měsíce</p>
                    </div>

                    <div className="mt-12 pt-10 border-t border-white/10">
                      <div className="flex items-center gap-4 mb-2">
                        <div className="p-2 bg-white/10 rounded-lg">
                          <ArrowRightLeft className="w-5 h-5" />
                        </div>
                        <p className="text-blue-100 text-sm italic leading-snug">
                          Cílový průměr na zbývajících <span className="font-bold text-white underline decoration-blue-400 underline-offset-4">{result.daysRemaining} dní</span>.
                        </p>
                      </div>
                    </div>
                  </div>

                  {/* Right Column: Breakdown */}
                  <div className="lg:col-span-3 p-10 space-y-8">
                    <div className="flex items-center justify-between mb-2">
                      <h3 className="text-2xl font-bold text-slate-800">Detailní rozpis dat</h3>
                      <div className="px-4 py-2 bg-slate-50 border border-slate-100 rounded-2xl flex items-center gap-2">
                        <div className="w-2 h-2 rounded-full bg-green-500"></div>
                        <span className="text-xs font-bold text-slate-500 uppercase tracking-wider">Data ověřena</span>
                      </div>
                    </div>

                    <div className="grid gap-4">
                      <DataPoint label="Revenue RR" value={result.revenueRR} color="blue" />
                      <DataPoint label="AR Asist Service Revenue" value={result.arAsistServiceRevenue} color="indigo" />
                      <DataPoint label="Service Asist Revenue" value={result.serviceAsistRevenue} color="slate" isNegative />
                    </div>

                    {/* Formula Explanation Section */}
                    <div className="mt-10 p-8 bg-slate-50 rounded-[2rem] border border-slate-100 relative overflow-hidden group">
                      <div className="absolute top-0 right-0 p-4 opacity-5 group-hover:opacity-10 transition-opacity">
                        <Calculator className="w-20 h-20 text-slate-900" />
                      </div>
                      <div className="flex items-start gap-5">
                        <div className="bg-white p-3 rounded-2xl shadow-sm border border-slate-200">
                          <Info className="w-6 h-6 text-blue-600" />
                        </div>
                        <div className="space-y-3">
                          <h4 className="text-lg font-bold text-slate-800">Logika výpočtu</h4>
                          <p className="text-slate-500 text-sm leading-relaxed">
                            Vzorec bere do úvahy <span className="font-bold">Revenue RR</span> násobené <span className="font-bold">AR Asist</span> servisem. Od tohoto základu odečítáme <span className="font-bold">Service Asist Revenue</span>. Čistý zbytek je rozdělen mezi zbývající dny.
                          </p>
                          <div className="flex items-center gap-2 font-mono text-xs p-3 bg-white rounded-xl border border-slate-200 text-blue-600">
                            <code>(({result.revenueRR} * {result.arAsistServiceRevenue}) - {result.serviceAsistRevenue}) / {result.daysRemaining}</code>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Empty State */}
        {!data.length && !isProcessing && !error && (
          <div className="py-24 flex flex-col items-center justify-center opacity-30">
            <div className="w-24 h-24 bg-slate-100 rounded-[2rem] flex items-center justify-center mb-6">
              <FileSpreadsheet className="w-12 h-12 text-slate-300" />
            </div>
            <p className="text-lg font-medium text-slate-400">Nahrajte Excel s údaji o pobočkách</p>
          </div>
        )}
      </main>

      <footer className="mt-24 pt-8 border-t border-slate-100 text-center">
        <p className="text-slate-400 text-sm font-medium">&copy; {new Date().getFullYear()} Analytický portál Budějovická</p>
      </footer>
    </div>
  );
};

const DataPoint = ({ label, value, color, isNegative }: { label: string, value: number, color: string, isNegative?: boolean }) => {
  const colorMap: any = {
