          const json = XLSX.utils.sheet_to_json<ExcelRow>(ws);

          const daysRemaining = getDaysRemainingInMonth();
          const targetBranch = "Budějovická";

          // Unikátní názvy pro debug
          const branches = Array.from(new Set(json.map(row => row.BranchName?.toString().trim()).filter(Boolean)));
          setAvailableBranches(branches as string[]);

          const results: CalculationResult[] = json
            .filter(row => {
              const bName = row.BranchName?.toString().trim();
              return bName?.toLowerCase() === targetBranch.toLowerCase();
            })
            .map(row => {
              // Ošetření číselných hodnot (může přijít string s čárkou nebo číslo)
              const parseVal = (val: any) => parseFloat(String(val || 0).replace(',', '.').replace(/\s/g, ''));

              const revRR = parseVal(row['Revenue RR']);
              const arAsist = parseVal(row['AR Asist Service Revenue']);
              const serAsist = parseVal(row['Service Asist Revenue']);

              // Vzorec: (Revenue RR * AR Asist Service Revenue - Service Asist Revenue) / dny
              const numerator = (revRR * arAsist) - serAsist;
              const finalValue = numerator / daysRemaining;

              return {
                branchName: targetBranch,
                revenueRR: revRR,
                arAsistServiceRevenue: arAsist,
                serviceAsistRevenue: serAsist,
                daysRemaining,
                finalValue,
                rawRow: row
              };
            });

          if (usedSheet !== targetSheetName) {
            setError(`Upozornění: List "${targetSheetName}" nebyl nalezen. Data byla načtena z listu "${usedSheet}".`);
          }

          if (results.length === 0) {
            setError(prev => (prev ? prev + " " : "") + `Pobočka "${targetBranch}" nebyla nalezena ve sloupci "BranchName".`);
          }

          setData(results);
        } catch (err) {
          setError("Chyba při dekódování Excelu. Ujistěte se, že soubor obsahuje sloupce BranchName, Revenue RR, atd.");
        } finally {
          setIsProcessing(false);
        }
      };
      reader.readAsArrayBuffer(file);
    } catch (err) {
      setError("Nepodařilo se otevřít soubor.");
      setIsProcessing(false);
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
