  AlertCircle, 
  CheckCircle2, 
  Info, 
  TrendingUp, 
  Calendar, 
  Search,
  ArrowRightLeft,
  Layers,
  FileText
} from 'lucide-react';
import { ExcelRow, CalculationResult } from './types';
import { getDaysRemainingInMonth, formatDate } from './utils/dateUtils';

const App: React.FC = () => {
  const [data, setData] = useState<CalculationResult[]>([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [fileName, setFileName] = useState<string | null>(null);
  const [availableBranches, setAvailableBranches] = useState<string[]>([]);
  const [activeSheetName, setActiveSheetName] = useState<string | null>(null);

  const processFile = async (file: File) => {
    setIsProcessing(true);
    setError(null);
    setFileName(file.name);
    setData([]);
    setAvailableBranches([]);
    setActiveSheetName(null);

    try {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const ab = e.target?.result;
          const wb = XLSX.read(ab, { type: 'array' });

          const targetSheetName = "Branch Performance";
          let ws = wb.Sheets[targetSheetName];
          let usedSheet = targetSheetName;

          // Kontrola existence listu, jinak fallback na první
          if (!ws) {
            usedSheet = wb.SheetNames[0];
            ws = wb.Sheets[usedSheet];
          }

          setActiveSheetName(usedSheet);

          // Převod na JSON objekty
          const json = XLSX.utils.sheet_to_json<ExcelRow>(ws);

          const daysRemaining = getDaysRemainingInMonth();
          const targetSearchTerm = "Budějovická";

          // Seznam všech poboček pro nápovědu při neúspěchu
          const branches = Array.from(new Set(json.map(row => {
            const val = row.BranchName || Object.values(row)[0]; // Fallback na první sloupec pokud BranchName chybí
            return val?.toString().trim();
          }).filter(Boolean)));
          setAvailableBranches(branches as string[]);

          const results: CalculationResult[] = json
            .filter(row => {
              // Hledáme v poli BranchName, nebo v prvním dostupném sloupci pokud BranchName není definováno
              const bName = (row.BranchName || Object.values(row)[0] || "").toString().trim();

              // FULLTEXTOVÉ VYHLEDÁVÁNÍ: case-insensitive hledání podřetězce
              return bName.toLowerCase().includes(targetSearchTerm.toLowerCase());
            })
            .map(row => {
              const parseVal = (val: any) => {
                if (typeof val === 'number') return val;
                if (!val) return 0;
                // Odstranění mezer (tisíce) a převod čárky na tečku
                const cleaned = String(val).replace(/\s/g, '').replace(',', '.');
                const num = parseFloat(cleaned);
                return isNaN(num) ? 0 : num;
              };

              const revRR = parseVal(row['Revenue RR']);
              const arAsist = parseVal(row['AR Asist Service Revenue']);
              const serAsist = parseVal(row['Service Asist Revenue']);

              // Výpočet: ((Revenue RR * AR Asist Service Revenue) - Service Asist Revenue) / dny
              const totalRevenue = (revRR * arAsist) - serAsist;
              const finalValue = totalRevenue / daysRemaining;

              return {
                branchName: (row.BranchName || Object.values(row)[0] || targetSearchTerm).toString(),
                revenueRR: revRR,
                arAsistServiceRevenue: arAsist,
                serviceAsistRevenue: serAsist,
                daysRemaining,
                finalValue,
                rawRow: row
              };
            });

          if (usedSheet !== targetSheetName) {
            setError(`Pozor: List "${targetSheetName}" nebyl nalezen. Používám list "${usedSheet}".`);
          }

          if (results.length === 0) {
            setError(prev => (prev ? prev + " | " : "") + `Žádná pobočka obsahující výraz "${targetSearchTerm}" nebyla nalezena.`);
          }

          setData(results);
        } catch (err) {
          console.error(err);
          setError("Chyba při dekódování Excelu. Zkontrolujte strukturu dat a názvy sloupců.");
        } finally {
          setIsProcessing(false);
        }
      };
      reader.readAsArrayBuffer(file);
    } catch (err) {
      setError("Nepodařilo se nahrát soubor.");
      setIsProcessing(false);
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      processFile(e.target.files[0]);
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-12 font-sans text-slate-900">
