export interface CalculationResult {
  branchName: string;
  revenueRR: number;
  planAsrServicesRevenue: number;
  serviceAsistRevenue: number;
  daysRemaining: number;
  finalValue: number;
  rawRow: any;
}

import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { 
  Upload, 
  Calculator, 
  AlertCircle, 
  CheckCircle2, 
  Info, 
  TrendingUp, 
  Calendar, 
  Search,
  ArrowRightLeft,
  Layers,
  FileText
} from 'lucide-react';
import { CalculationResult } from './types';
import { getDaysRemainingInMonth } from './utils/dateUtils';

const App: React.FC = () => {
  const [data, setData] = useState<CalculationResult[]>([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [fileName, setFileName] = useState<string | null>(null);
  const [availableBranches, setAvailableBranches] = useState<string[]>([]);
  const [activeSheetName, setActiveSheetName] = useState<string | null>(null);

  const processFile = async (file: File) => {
    setIsProcessing(true);
    setError(null);
    setFileName(file.name);
    setData([]);
    setAvailableBranches([]);
    setActiveSheetName(null);

    try {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const ab = e.target?.result;
          const wb = XLSX.read(ab, { type: 'array' });

          const targetSheetName = "Branch Performance";
          let ws = wb.Sheets[targetSheetName];
          let usedSheet = targetSheetName;

          if (!ws) {
            usedSheet = wb.SheetNames[0];
            ws = wb.Sheets[usedSheet];
          }
          setActiveSheetName(usedSheet);

          // Čteme jako 2D pole (matice), abychom mohli adresovat sloupce indexy (A=0, I=8, M=12, P=15)
          const rows: any[][] = XLSX.utils.sheet_to_json(ws, { header: 1 });

          const daysRemaining = getDaysRemainingInMonth();
          const targetSearchTerm = "Budějovická";

          // Sloupcové indexy (0-based)
          const COL_BRANCH = 0;  // A
          const COL_REV_RR = 8;  // I
          const COL_SERV_ASIST = 12; // M
          const COL_PLAN_ASR = 15; // P

          const branches: string[] = [];
          const results: CalculationResult[] = [];

          // Procházíme řádky (předpokládáme, že data mohou začínat kdekoliv, uživatel zmínil řádek 16)
          rows.forEach((row, index) => {
            const branchVal = row[COL_BRANCH]?.toString().trim();
            if (branchVal) {
              branches.push(branchVal);

              // Fulltextové hledání "Budějovická"
              if (branchVal.toLowerCase().includes(targetSearchTerm.toLowerCase())) {
                const parseVal = (val: any) => {
                  if (typeof val === 'number') return val;
                  if (!val) return 0;
                  const cleaned = String(val).replace(/\s/g, '').replace(',', '.');
                  const num = parseFloat(cleaned);
                  return isNaN(num) ? 0 : num;
                };

                const revRR = parseVal(row[COL_REV_RR]);
                const planAsr = parseVal(row[COL_PLAN_ASR]);
                const serAsist = parseVal(row[COL_SERV_ASIST]);

                // Vzorec: ((Revenue RR * Plan ASR Services/Revenue) - Service Asist Revenue) / dny
                const totalRevenue = (revRR * planAsr) - serAsist;
                const finalValue = totalRevenue / daysRemaining;

                results.push({
                  branchName: branchVal,
                  revenueRR: revRR,
                  planAsrServicesRevenue: planAsr,
                  serviceAsistRevenue: serAsist,
                  daysRemaining,
                  finalValue,
                  rawRow: row
                });
              }
            }
          });

          setAvailableBranches(Array.from(new Set(branches)));

          if (usedSheet !== targetSheetName) {
            setError(`Pozor: List "${targetSheetName}" nebyl nalezen. Používám list "${usedSheet}".`);
          }

          if (results.length === 0) {
            setError(prev => (prev ? prev + " | " : "") + `Pobočka obsahující "${targetSearchTerm}" nebyla nalezena ve sloupci A.`);
          }

          setData(results);
        } catch (err) {
          console.error(err);
          setError("Chyba při zpracování souboru. Ujistěte se, že jde o platný Excel.");
        } finally {
          setIsProcessing(false);
        }
      };
      reader.readAsArrayBuffer(file);
    } catch (err) {
      setError("Nepodařilo se nahrát soubor.");
      setIsProcessing(false);
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      processFile(e.target.files[0]);
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-12 font-sans text-slate-900">
      <header className="mb-12 flex flex-col items-center">
        <div className="bg-gradient-to-tr from-blue-600 to-indigo-600 p-4 rounded-3xl shadow-2xl shadow-blue-200 mb-6 transform hover:scale-105 transition-transform">
          <Calculator className="w-10 h-10 text-white" />
        </div>
        <h1 className="text-4xl font-black tracking-tight text-slate-900 mb-3 text-center">
          Analýza <span className="text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-indigo-600">Budějovické</span>
        </h1>
        <div className="flex flex-wrap justify-center gap-3">
          <div className="flex items-center gap-2 bg-white px-4 py-1.5 rounded-full border border-slate-100 shadow-sm text-slate-500">
            <Calendar className="w-4 h-4 text-blue-500" />
            <span className="text-sm font-medium">Do konce měsíce: <strong className="text-slate-800">{getDaysRemainingInMonth()} dní</strong></span>
          </div>
          {activeSheetName && (
            <div className="flex items-center gap-2 bg-indigo-50 px-4 py-1.5 rounded-full border border-indigo-100 shadow-sm text-indigo-700">
              <Layers className="w-4 h-4" />
              <span className="text-sm font-bold">List: {activeSheetName}</span>
            </div>
          )}
        </div>
      </header>

      <main className="space-y-10">
        <section>
          <div className={`relative group border-4 border-dashed rounded-[2.5rem] p-16 transition-all duration-500 text-center
            ${fileName ? 'border-green-200 bg-green-50/30' : 'border-slate-200 bg-white hover:border-blue-400 hover:shadow-2xl hover:shadow-blue-50'}`}>
            <input
              type="file"
              accept=".xlsx, .xls"
              onChange={handleFileChange}
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
            />
            <div className="flex flex-col items-center gap-6">
              <div className={`p-6 rounded-full shadow-inner transition-all duration-500 group-hover:scale-110 
                ${fileName ? 'bg-green-100 text-green-600' : 'bg-blue-50 text-blue-500'}`}>
                {fileName ? <CheckCircle2 className="w-12 h-12" /> : <FileText className="w-12 h-12" />}
              </div>
              <div className="max-w-sm">
                <p className="text-2xl font-bold text-slate-800 mb-2">
                  {fileName ? fileName : 'Nahrajte Excel report'}
                </p>
                <p className="text-slate-400 italic">Hledám sloupce A, I, P, M v listu "Branch Performance"</p>
              </div>
            </div>
          </div>
        </section>

        {error && (
          <div className="animate-in fade-in slide-in-from-top-4 duration-500">
            <div className="bg-amber-50 border-2 border-amber-100 rounded-3xl p-8 flex flex-col gap-6">
              <div className="flex items-center gap-4 text-amber-700">
                <AlertCircle className="w-8 h-8 flex-shrink-0" />
                <h3 className="text-xl font-bold">{error}</h3>
              </div>
              {availableBranches.length > 0 && (
                <div className="bg-white/60 rounded-2xl p-6">
                  <p className="text-xs font-bold text-slate-400 uppercase tracking-widest mb-4">Pobočky nalezené ve sloupci A:</p>
                  <div className="flex flex-wrap gap-2">
                    {availableBranches.slice(0, 10).map((b, i) => (
                      <span key={i} className="px-3 py-1 bg-slate-100 text-slate-600 rounded-lg text-xs font-medium border border-slate-200">{b}</span>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        )}

        {isProcessing && (
          <div className="flex flex-col items-center justify-center py-20 gap-4">
            <div className="w-16 h-16 border-4 border-slate-100 border-t-blue-600 rounded-full animate-spin"></div>
            <p className="text-slate-400 font-medium animate-pulse">Přepočítávám cíle...</p>
          </div>
        )}

        {data.length > 0 && !isProcessing && (
          <div className="animate-in zoom-in-95 fade-in duration-700 space-y-10">
            {data.map((result, idx) => (
              <div key={idx} className="bg-white rounded-[3rem] shadow-2xl shadow-slate-200/50 border border-slate-100 overflow-hidden">
                <div className="grid lg:grid-cols-5">
                  <div className="lg:col-span-2 bg-gradient-to-br from-blue-800 to-indigo-900 p-12 text-white flex flex-col justify-between">
                    <div>
                      <div className="flex items-center gap-3 mb-10 opacity-70">
                        <TrendingUp className="w-5 h-5" />
                        <span className="text-xs font-black uppercase tracking-[0.2em]">Denní průměr</span>
                      </div>
                      <h2 className="text-8xl font-black mb-4 tracking-tighter">
                        {Math.round(result.finalValue).toLocaleString('cs-CZ')}
                      </h2>
                      <p className="text-blue-100 text-xl font-medium">CZK / den</p>
                      <p className="mt-4 text-xs bg-white/10 w-fit px-3 py-1 rounded-lg border border-white/10 font-bold">
                        {result.branchName}
                      </p>
                    </div>

                    <div className="mt-12 pt-10 border-t border-white/10 flex items-center gap-4">
                      <div className="bg-white/10 p-3 rounded-2xl">
                        <ArrowRightLeft className="w-6 h-6 text-blue-200" />
                      </div>
                      <p className="text-sm text-blue-100 leading-tight">
                        Výpočet na zbývajících <span className="text-white font-bold">{result.daysRemaining} dní</span> měsíce.
                      </p>
                    </div>
                  </div>

                  <div className="lg:col-span-3 p-12 space-y-10">
                    <h3 className="text-2xl font-bold text-slate-800 flex items-center gap-3">
                      Zdrojové hodnoty
                      <span className="text-xs bg-blue-100 text-blue-600 px-3 py-1 rounded-full">Sloupce A, I, P, M</span>
                    </h3>

                    <div className="grid gap-6">
                      <ValueItem label="Revenue RR (Col I)" value={result.revenueRR} />
                      <ValueItem label="Plan ASR Services/Revenue (Col P)" value={result.planAsrServicesRevenue} />
                      <ValueItem label="Service Asist Revenue (Col M)" value={result.serviceAsistRevenue} isMinus />
                    </div>

                    <div className="bg-slate-50 rounded-[2rem] p-8 border border-slate-100">
                      <div className="flex items-start gap-4 mb-4 text-blue-600">
                        <Info className="w-5 h-5 mt-1" />
                        <h4 className="font-bold text-slate-800 uppercase text-xs tracking-widest">Matematický model</h4>
                      </div>
                      <div className="bg-white p-5 rounded-2xl border border-slate-200 font-mono text-sm text-blue-600 overflow-x-auto shadow-sm">
                        <code>(({result.revenueRR.toLocaleString()} * {result.planAsrServicesRevenue.toLocaleString()}) - {result.serviceAsistRevenue.toLocaleString()}) / {result.daysRemaining}</code>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </main>

      <footer className="mt-24 pt-8 border-t border-slate-100 text-center text-slate-400 text-[10px] uppercase tracking-[0.2em] font-bold">
        Optimalizováno pro sloupce A, I, P, M | {new Date().getFullYear()}
      </footer>
    </div>
  );
};

