  const [availableBranches, setAvailableBranches] = useState<string[]>([]);
  const [activeSheetName, setActiveSheetName] = useState<string | null>(null);

  const processFile = async (file: File) => {
    setIsProcessing(true);
    setError(null);
    setFileName(file.name);
    setData([]);
    setAvailableBranches([]);
    setActiveSheetName(null);

    try {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const ab = e.target?.result;
          const wb = XLSX.read(ab, { type: 'array' });

          const targetSheetName = "Branch Performance";
          let ws = wb.Sheets[targetSheetName];
          let usedSheet = targetSheetName;

          if (!ws) {
            usedSheet = wb.SheetNames[0];
            ws = wb.Sheets[usedSheet];
          }
          setActiveSheetName(usedSheet);

          // Čteme jako 2D pole (matice), abychom mohli adresovat sloupce indexy
          // A=0, B=1, I=8, M=12, P=15
          const rows: any[][] = XLSX.utils.sheet_to_json(ws, { header: 1 });

          const daysRemaining = getDaysRemainingInMonth();
          const targetSearchTerm = "Budějovická";

          // Sloupcové indexy (0-based)
          const COL_BRANCH = 1;      // B (Pobočka)
          const COL_REV_RR = 8;      // I (Revenue RR)
          const COL_SERV_ASIST = 12; // M (Service Asist Revenue)
          const COL_PLAN_ASR = 15;   // P (Plan ASR Services/Revenue)

          const branches: string[] = [];
          const results: CalculationResult[] = [];

          rows.forEach((row, index) => {
            const branchVal = row[COL_BRANCH]?.toString().trim();

            if (branchVal) {
              branches.push(branchVal);

              if (branchVal.toLowerCase().includes(targetSearchTerm.toLowerCase())) {
                const parseVal = (val: any) => {
                  if (typeof val === 'number') return val;
                  if (!val) return 0;
                  const cleaned = String(val).replace(/\s/g, '').replace(',', '.');
                  const num = parseFloat(cleaned);
                  return isNaN(num) ? 0 : num;
                };

                const revRR = parseVal(row[COL_REV_RR]);
                const planAsr = parseVal(row[COL_PLAN_ASR]);
                const serAsist = parseVal(row[COL_SERV_ASIST]);

                // Vzorec: ((Revenue RR * Plan ASR Services/Revenue) - Service Asist Revenue) / dny
                const totalRevenue = (revRR * planAsr) - serAsist;
                const finalValue = totalRevenue / daysRemaining;

                results.push({
                  branchName: branchVal,
                  revenueRR: revRR,
                  planAsrServicesRevenue: planAsr,
                  serviceAsistRevenue: serAsist,
                  daysRemaining,
                  finalValue,
                  rawRow: row
                });
              }
            }
          });

          setAvailableBranches(Array.from(new Set(branches)));

          if (usedSheet !== targetSheetName) {
            setError(`Pozor: List "${targetSheetName}" nebyl nalezen. Používám list "${usedSheet}".`);
          }

          if (results.length === 0) {
            setError(prev => (prev ? prev + " | " : "") + `Pobočka obsahující "${targetSearchTerm}" nebyla nalezena ve sloupci B.`);
          }

          setData(results);
        } catch (err) {
          console.error(err);
          setError("Chyba při zpracování souboru. Ujistěte se, že jde o platný Excel.");
        } finally {
          setIsProcessing(false);
        }
      };
      reader.readAsArrayBuffer(file);
    } catch (err) {
      setError("Nepodařilo se nahrát soubor.");
      setIsProcessing(false);
    }
  };

