const ValueItem = ({ label, value, isMinus, isPercentage }: { label: string, value: number, isMinus?: boolean, isPercentage?: boolean }) => {
  const displayValue = isPercentage ? (value * 100) : value;
  const formattedValue = displayValue.toLocaleString('cs-CZ', {
    minimumFractionDigits: isPercentage ? 2 : 0,
