import { getDaysRemainingInMonth } from './utils/dateUtils';

const App: React.FC = () => {
  const [allCalculations, setAllCalculations] = useState<CalculationResult[]>([]);
  const [selectedBranch, setSelectedBranch] = useState<string>("");
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [fileName, setFileName] = useState<string | null>(null);
  const [activeSheetName, setActiveSheetName] = useState<string | null>(null);

  const availableBranches = useMemo(() => {
    return Array.from(new Set(allCalculations.map(c => c.branchName))).sort();
  }, [allCalculations]);

  const filteredResult = useMemo(() => {
    if (!selectedBranch) return null;
    return allCalculations.find(c => c.branchName === selectedBranch) || null;
  }, [allCalculations, selectedBranch]);

  const processFile = async (file: File) => {
    setIsProcessing(true);
    setError(null);
    setFileName(file.name);
    setAllCalculations([]);
    setSelectedBranch("");
    setActiveSheetName(null);

    try {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const ab = e.target?.result;
          const wb = XLSX.read(ab, { type: 'array' });

          const targetSheetName = "Branch Performance";
          let ws = wb.Sheets[targetSheetName];
          let usedSheet = targetSheetName;

          if (!ws) {
            usedSheet = wb.SheetNames[0];
            ws = wb.Sheets[usedSheet];
          }
          setActiveSheetName(usedSheet);

          const rows: any[][] = XLSX.utils.sheet_to_json(ws, { header: 1 });
          const daysRemaining = getDaysRemainingInMonth();

          const COL_BRANCH = 1;      // B
          const COL_REV_RR = 8;      // I
          const COL_SERV_ASIST = 12; // M
          const COL_PLAN_ASR = 15;   // P

          const results: CalculationResult[] = [];

          rows.forEach((row) => {
            const branchVal = row[COL_BRANCH]?.toString().trim();

            if (branchVal && branchVal !== "BranchName" && branchVal !== "Pobočka") {
              const parseVal = (val: any) => {
                if (typeof val === 'number') return val;
                if (!val) return 0;
                const cleaned = String(val).replace(/\s/g, '').replace(',', '.');
                const num = parseFloat(cleaned);
                return isNaN(num) ? 0 : num;
              };

              const revRR = parseVal(row[COL_REV_RR]);
              const planAsr = parseVal(row[COL_PLAN_ASR]);
              const serAsist = parseVal(row[COL_SERV_ASIST]);

              if (revRR === 0 && planAsr === 0 && serAsist === 0) return;

              const totalRevenue = (revRR * planAsr) - serAsist;
              const finalValue = totalRevenue / daysRemaining;

              results.push({
                branchName: branchVal,
                revenueRR: revRR,
                planAsrServicesRevenue: planAsr,
                serviceAsistRevenue: serAsist,
                daysRemaining,
                finalValue,
                rawRow: row
              });
