            <div className="bg-white rounded-[3rem] shadow-2xl shadow-slate-200/50 border border-slate-100 overflow-hidden print:border-none print:shadow-none print:rounded-none">
              <div className="grid lg:grid-cols-5">
                <div className="lg:col-span-2 bg-gradient-to-br from-blue-800 to-indigo-900 p-12 text-white flex flex-col justify-between print:bg-none print:bg-slate-900 print:text-white">
                  <div>
                    <div className="flex items-center gap-3 mb-10 opacity-70">
                      <TrendingUp className="w-5 h-5" />
                      <span className="text-xs font-black uppercase tracking-[0.2em]">Denní plán - služby</span>
                    </div>
                    <h2 className="text-8xl font-black mb-4 tracking-tighter print:text-6xl">
                      {Math.round(filteredResult.finalValue).toLocaleString('cs-CZ')}
                    </h2>
                    <p className="text-blue-100 text-xl font-medium print:text-blue-200">CZK / den</p>
                    <div className="mt-4 flex flex-col gap-1">
                      <p className="text-xs bg-white/10 w-fit px-3 py-1 rounded-lg border border-white/10 font-bold print:bg-slate-800 print:text-white print:border-none">
                        {filteredResult.branchName}
                      </p>
                    </div>
                  </div>

                  <div className="mt-12 pt-10 border-t border-white/10 flex items-center gap-4 print:border-slate-800">
                    <p className="text-sm text-blue-100 leading-tight print:text-blue-300">
                      Zbývá <span className="text-white font-bold">{filteredResult.daysRemaining} dní</span> měsíce.
                    </p>
