const ValueItem = ({ label, value, isMinus, isPercentage }: { label: string, value: number, isMinus?: boolean, isPercentage?: boolean }) => {
  const displayValue = isPercentage ? (value * 100) : value;
  const formattedValue = displayValue.toLocaleString('cs-CZ', {
    minimumFractionDigits: isPercentage ? 2 : 0,
    maximumFractionDigits: isPercentage ? 6 : 0,
  });

  return (
    <div className="flex items-center justify-between p-5 bg-white border border-slate-100 rounded-3xl hover:border-blue-200 transition-all print:border-slate-200 print:p-4 print:rounded-2xl">
      <span className="text-slate-500 font-semibold print:text-slate-700">{label}</span>
      <div className="text-right">
        <span className={`text-2xl font-black print:text-xl ${isMinus ? 'text-rose-500' : 'text-slate-900'}`}>
          {isMinus && "− "}
          {formattedValue}
        </span>
        <span className="text-[10px] text-slate-300 ml-3 font-black uppercase print:text-slate-500">
          {isPercentage ? '%' : 'CZK'}
        </span>
      </div>
    </div>
  );
};

