import React, { useState, useMemo } from 'react';
import * as XLSX from 'xlsx';
import { 
  Calculator, 
  AlertCircle, 
  CheckCircle2, 
  Info, 
  TrendingUp, 
  Calendar, 
  Layers,
  FileText,
  ChevronDown,
  Printer
} from 'lucide-react';
import { CalculationResult } from './types';
import { getDaysRemainingInMonth } from './utils/dateUtils';

const App: React.FC = () => {
  const [allCalculations, setAllCalculations] = useState<CalculationResult[]>([]);
  const [selectedBranch, setSelectedBranch] = useState<string>("");
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [fileName, setFileName] = useState<string | null>(null);
  const [activeSheetName, setActiveSheetName] = useState<string | null>(null);

  const availableBranches = useMemo(() => {
    return Array.from(new Set(allCalculations.map(c => c.branchName))).sort();
  }, [allCalculations]);

  const filteredResult = useMemo(() => {
    if (!selectedBranch) return null;
    return allCalculations.find(c => c.branchName === selectedBranch) || null;
  }, [allCalculations, selectedBranch]);

  const processFile = async (file: File) => {
    setIsProcessing(true);
    setError(null);
    setFileName(file.name);
    setAllCalculations([]);
    setSelectedBranch("");
    setActiveSheetName(null);

    try {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const ab = e.target?.result;
          const wb = XLSX.read(ab, { type: 'array' });

          const targetSheetName = "Branch Performance";
          let ws = wb.Sheets[targetSheetName];
          let usedSheet = targetSheetName;

          if (!ws) {
            usedSheet = wb.SheetNames[0];
            ws = wb.Sheets[usedSheet];
          }
          setActiveSheetName(usedSheet);

          const rows: any[][] = XLSX.utils.sheet_to_json(ws, { header: 1 });
          if (rows.length < 1) {
            throw new Error("Soubor neobsahuje žádná data.");
          }

          const headerRow = rows[0];

          // Dynamické vyhledání indexů sloupců podle názvu
          const findColIndex = (names: string[]) => {
            return headerRow.findIndex(cell => 
              cell && names.some(name => String(cell).toLowerCase().trim() === name.toLowerCase().trim())
            );
          };

          const colIdxBranch = findColIndex(['BranchName', 'Pobočka']);
          const colIdxRevRR = findColIndex(['Revenue RR']);
          const colIdxServAsist = findColIndex(['Service Asist Revenue']);
          const colIdxPlanAsr = findColIndex(['Plan ASR Services/Revenue']);

          // Kontrola, zda byly nalezeny všechny potřebné sloupce
          const missingCols = [];
          if (colIdxBranch === -1) missingCols.push('BranchName');
          if (colIdxRevRR === -1) missingCols.push('Revenue RR');
          if (colIdxServAsist === -1) missingCols.push('Service Asist Revenue');
          if (colIdxPlanAsr === -1) missingCols.push('Plan ASR Services/Revenue');

          if (missingCols.length > 0) {
            setError(`V souboru chybí tyto sloupce: ${missingCols.join(', ')}`);
            setIsProcessing(false);
            return;
          }

          const daysRemaining = getDaysRemainingInMonth();
          const results: CalculationResult[] = [];

          // Zpracování dat od druhého řádku dále
          for (let i = 1; i < rows.length; i++) {
            const row = rows[i];
            const branchVal = row[colIdxBranch]?.toString().trim();

            if (branchVal && branchVal !== "BranchName" && branchVal !== "Pobočka") {
              const parseVal = (val: any) => {
                if (typeof val === 'number') return val;
                if (!val) return 0;
                const cleaned = String(val).replace(/\s/g, '').replace(',', '.');
                const num = parseFloat(cleaned);
                return isNaN(num) ? 0 : num;
              };

              const revRR = parseVal(row[colIdxRevRR]);
              const planAsr = parseVal(row[colIdxPlanAsr]);
              const serAsist = parseVal(row[colIdxServAsist]);

              if (revRR === 0 && planAsr === 0 && serAsist === 0) continue;

              const totalRevenue = (revRR * planAsr) - serAsist;
              const finalValue = totalRevenue / daysRemaining;

              results.push({
                branchName: branchVal,
                revenueRR: revRR,
                planAsrServicesRevenue: planAsr,
                serviceAsistRevenue: serAsist,
                daysRemaining,
                finalValue,
                rawRow: row
              });
            }
          }

          if (results.length === 0) {
            setError(`V souboru nebyla nalezena žádná platná data pro výpočet.`);
          } else {
            setAllCalculations(results);
            const budejovicka = results.find(r => r.branchName.toLowerCase().includes("budějovická"));
            setSelectedBranch(budejovicka ? budejovicka.branchName : results[0].branchName);
          }

        } catch (err: any) {
          setError(err.message || "Chyba při zpracování souboru.");
        } finally {
          setIsProcessing(false);
        }
      };
      reader.readAsArrayBuffer(file);
    } catch (err) {
      setError("Nepodařilo se nahrát soubor.");
      setIsProcessing(false);
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      processFile(e.target.files[0]);
    }
  };

  const handlePrint = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    // Používáme prověřený přístup pro vyvolání tiskového dialogu
    try {
      window.print();
    } catch (err) {
      alert("Tisk se nepodařilo spustit. Zkuste prosím klávesovou zkratku Ctrl+P (nebo Cmd+P na Macu).");
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-12 font-sans text-slate-900">
      <style>{`
        @media print {
          body { background: white !important; padding: 0 !important; margin: 0 !important; -webkit-print-color-adjust: exact !important; print-color-adjust: exact !important; }
          .no-print { display: none !important; }
          .print-full { width: 100% !important; max-width: 100% !important; margin: 0 !important; padding: 0 !important; border: none !important; box-shadow: none !important; }
          @page { size: A4; margin: 1cm; }
          .print-only { display: block !important; }
        }
        .print-only { display: none; }
      `}</style>

      <header className="mb-12 flex flex-col items-center no-print">
        <div className="bg-gradient-to-tr from-blue-600 to-indigo-600 p-4 rounded-3xl shadow-2xl shadow-blue-200 mb-6 transform hover:scale-105 transition-transform">
          <Calculator className="w-10 h-10 text-white" />
        </div>
        <h1 className="text-4xl font-black tracking-tight text-slate-900 mb-3 text-center">
          Služby - <span className="text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-indigo-600">denní plánování</span>
        </h1>
        <div className="flex flex-wrap justify-center gap-3">
          <div className="flex items-center gap-2 bg-white px-4 py-1.5 rounded-full border border-slate-100 shadow-sm text-slate-500">
            <Calendar className="w-4 h-4 text-blue-500" />
            <span className="text-sm font-medium">Do konce měsíce: <strong className="text-slate-800">{getDaysRemainingInMonth()} dní</strong></span>
          </div>
          {activeSheetName && (
            <div className="flex items-center gap-2 bg-indigo-50 px-4 py-1.5 rounded-full border border-indigo-100 shadow-sm text-indigo-700">
              <Layers className="w-4 h-4" />
              <span className="text-sm font-bold">List: {activeSheetName}</span>
            </div>
          )}
        </div>
      </header>

      {/* Viditelné pouze při tisku */}
      <div className="print-only flex-col items-center mb-8 border-b pb-6 text-center">
        <h1 className="text-2xl font-bold text-slate-900">Denní plán služeb</h1>
        <p className="text-slate-500 font-medium">Report generován dne {new Date().toLocaleDateString('cs-CZ')}</p>
      </div>

      <main className="space-y-10">
        <section className="no-print">
          <div className={`relative group border-4 border-dashed rounded-[2.5rem] p-16 transition-all duration-500 text-center
            ${fileName ? 'border-green-200 bg-green-50/30' : 'border-slate-200 bg-white hover:border-blue-400 hover:shadow-2xl hover:shadow-blue-50'}`}>
            <input
              type="file"
              accept=".xlsx, .xls"
              onChange={handleFileChange}
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
            />
            <div className="flex flex-col items-center gap-6">
              <div className={`p-6 rounded-full shadow-inner transition-all duration-500 group-hover:scale-110 
                ${fileName ? 'bg-green-100 text-green-600' : 'bg-blue-50 text-blue-500'}`}>
                {fileName ? <CheckCircle2 className="w-12 h-12" /> : <FileText className="w-12 h-12" />}
              </div>
              <div className="max-w-sm">
                <p className="text-2xl font-bold text-slate-800 mb-2">
                  {fileName ? fileName : 'Nahrajte Excel report'}
                </p>
                <p className="text-slate-400 italic">Sloupce budou vyhledány podle názvů</p>
              </div>
            </div>
          </div>
        </section>

        {allCalculations.length > 0 && !isProcessing && (
          <section className="animate-in fade-in slide-in-from-bottom-4 duration-500 max-w-2xl mx-auto no-print">
            <div className="bg-white rounded-[2rem] p-6 shadow-xl border border-slate-100 flex flex-col sm:flex-row gap-4">
              <div className="flex-grow">
                <label className="block text-xs font-black text-slate-400 uppercase tracking-widest mb-3 ml-2">Vybrat pobočku</label>
                <div className="relative">
                  <select 
                    value={selectedBranch}
                    onChange={(e) => setSelectedBranch(e.target.value)}
                    className="w-full appearance-none bg-slate-50 border-2 border-slate-100 rounded-2xl px-6 py-4 font-bold text-lg focus:outline-none focus:border-blue-500 transition-all cursor-pointer"
                  >
                    {availableBranches.map(branch => (
                      <option key={branch} value={branch}>{branch}</option>
                    ))}
                  </select>
                  <div className="absolute right-6 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400">
                    <ChevronDown className="w-6 h-6" />
                  </div>
                </div>
              </div>
              <div className="flex items-end">
                <button 
                  type="button"
                  onClick={handlePrint}
                  className="bg-slate-900 hover:bg-black text-white rounded-2xl px-8 py-4 flex items-center gap-3 font-bold transition-all shadow-lg active:scale-95 cursor-pointer z-20"
                >
                  <Printer className="w-5 h-5" />
                  <span>Tisk</span>
                </button>
              </div>
            </div>
          </section>
        )}

        {error && (
          <div className="animate-in fade-in slide-in-from-top-4 duration-500 no-print">
            <div className="bg-rose-50 border-2 border-rose-100 rounded-3xl p-8 flex flex-col gap-6">
              <div className="flex items-center gap-4 text-rose-700">
                <AlertCircle className="w-8 h-8 flex-shrink-0" />
                <h3 className="text-xl font-bold">{error}</h3>
              </div>
              <p className="text-rose-600/80 text-sm">Zkontrolujte prosím názvy sloupců v prvním řádku Vašeho Excelu.</p>
            </div>
          </div>
        )}

        {isProcessing && (
          <div className="flex flex-col items-center justify-center py-20 gap-4 no-print">
            <div className="w-16 h-16 border-4 border-slate-100 border-t-blue-600 rounded-full animate-spin"></div>
            <p className="text-slate-400 font-medium animate-pulse">Analyzuji strukturu souboru...</p>
          </div>
        )}

        {filteredResult && !isProcessing && (
          <div className="animate-in zoom-in-95 fade-in duration-700 space-y-10 print-full">
            <div className="bg-white rounded-[3rem] shadow-2xl shadow-slate-200/50 border border-slate-100 overflow-hidden print:border-none print:shadow-none print:rounded-none">
              <div className="grid lg:grid-cols-5">
                <div className="lg:col-span-2 bg-gradient-to-br from-slate-800 to-slate-950 p-12 text-white flex flex-col justify-between print:bg-none print:bg-slate-900 print:text-white">
                  <div>
                    <div className="flex items-center gap-3 mb-10 opacity-70">
                      <TrendingUp className="w-5 h-5" />
                      <span className="text-xs font-black uppercase tracking-[0.2em]">Denní plán - služby</span>
                    </div>
                    <h2 className="text-8xl font-black mb-4 tracking-tighter print:text-6xl">
                      {Math.round(filteredResult.finalValue).toLocaleString('cs-CZ')}
                    </h2>
                    <p className="text-slate-300 text-xl font-medium">CZK / den</p>
                    <div className="mt-6">
                      <p className="text-sm bg-white/10 w-fit px-4 py-2 rounded-xl border border-white/10 font-bold">
                        {filteredResult.branchName}
                      </p>
                    </div>
                  </div>

                  <div className="mt-12 pt-10 border-t border-white/10 flex items-center gap-4">
                    <p className="text-sm text-slate-300 leading-tight">
                      Zbývá <span className="text-white font-bold">{filteredResult.daysRemaining} dní</span> do konce měsíce.
                    </p>
                  </div>
                </div>

                <div className="lg:col-span-3 p-12 space-y-10 print:p-8">
                  <h3 className="text-2xl font-bold text-slate-800 flex items-center gap-3 no-print">
                    Načtená data
                  </h3>

                  <div className="grid gap-6">
                    <ValueItem label="Revenue RR" value={filteredResult.revenueRR} />
                    <ValueItem label="Plan ASR Services/Revenue" value={filteredResult.planAsrServicesRevenue} isPercentage />
                    <ValueItem label="Service Asist Revenue" value={filteredResult.serviceAsistRevenue} isMinus />
                  </div>

                  <div className="bg-slate-50 rounded-[2rem] p-8 border border-slate-100 print:bg-white print:p-4 print:border-slate-200">
                    <div className="flex items-start gap-4 mb-4 text-slate-400 no-print">
                      <Info className="w-5 h-5 mt-1" />
                      <h4 className="font-bold text-slate-600 uppercase text-xs tracking-widest">Kontrolní výpočet</h4>
                    </div>
                    <div className="bg-white p-5 rounded-2xl border border-slate-200 font-mono text-xs text-slate-500 overflow-x-auto shadow-sm print:shadow-none print:p-2">
                      <code>(({filteredResult.revenueRR.toLocaleString('cs-CZ')} * {(filteredResult.planAsrServicesRevenue * 100).toLocaleString('cs-CZ', { maximumFractionDigits: 4 })}%) - {filteredResult.serviceAsistRevenue.toLocaleString('cs-CZ')}) / {filteredResult.daysRemaining}</code>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>

      <footer className="mt-24 pt-8 border-t border-slate-100 text-center text-slate-400 text-[10px] uppercase tracking-[0.2em] font-bold no-print">
        Plánovač služeb | {new Date().toLocaleDateString('cs-CZ')}
      </footer>
    </div>
  );
};

const ValueItem = ({ label, value, isMinus, isPercentage }: { label: string, value: number, isMinus?: boolean, isPercentage?: boolean }) => {
  const displayValue = isPercentage ? (value * 100) : value;
  const formattedValue = displayValue.toLocaleString('cs-CZ', {
    minimumFractionDigits: isPercentage ? 2 : 0,
    maximumFractionDigits: isPercentage ? 6 : 0,
  });

  return (
