            <div className="bg-white rounded-[3rem] shadow-2xl shadow-slate-200/50 border border-slate-100 overflow-hidden print:border-none print:shadow-none print:rounded-none">
              <div className="grid lg:grid-cols-5 print-card-layout">
                {/* LEVÝ PANEL - TMAVÝ */}
                <div className="lg:col-span-2 bg-slate-900 p-12 text-white flex flex-col justify-between print-sidebar">
                  <div>
                    <div className="flex items-center gap-3 mb-10 opacity-70">
                      <TrendingUp className="w-5 h-5" />
                      <span className="text-xs font-black uppercase tracking-[0.2em]">Denní plán - služby</span>
                    </div>
                    <h2 className="text-8xl font-black mb-4 tracking-tighter">
                      {Math.round(filteredResult.finalValue).toLocaleString('cs-CZ')}
                    </h2>
