        Vytvořeno pro pobočku {selectedBranch || '...'} | {new Date().toLocaleDateString('cs-CZ')}
      </footer>
    </div>
  );
};

const ValueItem = ({ label, value, isMinus, isPercentage }: { label: string, value: number, isMinus?: boolean, isPercentage?: boolean }) => {
  const displayValue = isPercentage ? (value * 100) : value;
  const formattedValue = displayValue.toLocaleString('cs-CZ', {
    minimumFractionDigits: isPercentage ? 2 : 0,
    maximumFractionDigits: isPercentage ? 2 : 0,
  });

  return (
    <div className="flex items-center justify-between p-6 bg-white border border-slate-100 rounded-[1.5rem] shadow-sm">
      <span className="text-slate-400 font-bold text-sm">{label}</span>
      <div className="flex items-baseline gap-2">
        <span className={`text-2xl font-black ${isMinus ? 'text-rose-500' : 'text-slate-900'}`}>
          {isMinus && "− "}
          {formattedValue}
        </span>
        <span className="text-[10px] text-slate-300 font-black uppercase">
          {isPercentage ? '%' : 'CZK'}
        </span>
      </div>
    </div>
  );
};

