import React, { useState, useMemo } from 'react';
import * as XLSX from 'xlsx';
import { 
  Calculator, 
  AlertCircle, 
  CheckCircle2, 
  Info, 
  TrendingUp, 
  Calendar, 
  Layers,
  FileText,
  ChevronDown,
  Printer
} from 'lucide-react';
import { CalculationResult } from './types';
import { getDaysRemainingInMonth } from './utils/dateUtils';

const App: React.FC = () => {
  const [allCalculations, setAllCalculations] = useState<CalculationResult[]>([]);
  const [selectedBranch, setSelectedBranch] = useState<string>("");
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [fileName, setFileName] = useState<string | null>(null);
  const [activeSheetName, setActiveSheetName] = useState<string | null>(null);

  const availableBranches = useMemo(() => {
    return Array.from(new Set(allCalculations.map(c => c.branchName))).sort();
  }, [allCalculations]);

  const filteredResult = useMemo(() => {
    if (!selectedBranch) return null;
    return allCalculations.find(c => c.branchName === selectedBranch) || null;
  }, [allCalculations, selectedBranch]);

  const processFile = async (file: File) => {
    setIsProcessing(true);
    setError(null);
    setFileName(file.name);
    setAllCalculations([]);
    setSelectedBranch("");

    try {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const ab = e.target?.result;
          const wb = XLSX.read(ab, { type: 'array' });

          const targetSheetName = "Branch Performance";
          let ws = wb.Sheets[targetSheetName];
          if (!ws) ws = wb.Sheets[wb.SheetNames[0]];

          setActiveSheetName(wb.SheetNames[0]);

          const rows: any[][] = XLSX.utils.sheet_to_json(ws, { header: 1 });

          if (rows.length < 5) {
            throw new Error("Soubor je příliš krátký. Záhlaví musí být na 5. řádku.");
          }

          const headerRow = rows[4]; // 5. řádek (index 4)

          const findColIndex = (names: string[]) => {
            return headerRow.findIndex(cell => 
              cell && names.some(name => String(cell).toLowerCase().trim() === name.toLowerCase().trim())
            );
          };

          const colIdxBranch = findColIndex(['BranchName', 'Pobočka']);
          const colIdxRevRR = findColIndex(['Revenue RR']);
          const colIdxServAsist = findColIndex(['Service Asist Revenue']);
          const colIdxPlanAsr = findColIndex(['Plan ASR Services/Revenue']);

          if (colIdxBranch === -1 || colIdxRevRR === -1 || colIdxServAsist === -1 || colIdxPlanAsr === -1) {
            setError("Nepodařilo se najít všechny sloupce na 5. řádku. Zkontrolujte názvy.");
            setIsProcessing(false);
            return;
          }

          const daysRemaining = getDaysRemainingInMonth();
          const results: CalculationResult[] = [];

          for (let i = 5; i < rows.length; i++) {
            const row = rows[i];
            const branchVal = row[colIdxBranch]?.toString().trim();

            if (branchVal) {
              const parseVal = (val: any) => {
                if (typeof val === 'number') return val;
                if (!val) return 0;
                const cleaned = String(val).replace(/\s/g, '').replace(',', '.');
                const num = parseFloat(cleaned);
                return isNaN(num) ? 0 : num;
              };

              const revRR = parseVal(row[colIdxRevRR]);
              const planAsr = parseVal(row[colIdxPlanAsr]);
              const serAsist = parseVal(row[colIdxServAsist]);

              if (revRR === 0 && planAsr === 0) continue;

              const totalRevenue = (revRR * planAsr) - serAsist;
              const finalValue = totalRevenue / daysRemaining;

              results.push({
                branchName: branchVal,
                revenueRR: revRR,
                planAsrServicesRevenue: planAsr,
                serviceAsistRevenue: serAsist,
                daysRemaining,
                finalValue,
                rawRow: row
              });
            }
          }

          setAllCalculations(results);
          const budejovicka = results.find(r => r.branchName.toLowerCase().includes("budějovická"));
          setSelectedBranch(budejovicka ? budejovicka.branchName : (results[0]?.branchName || ""));

        } catch (err: any) {
          setError(err.message);
        } finally {
          setIsProcessing(false);
        }
      };
      reader.readAsArrayBuffer(file);
    } catch (err) {
      setError("Chyba při nahrávání.");
      setIsProcessing(false);
    }
  };

  const handlePrint = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    // Timeout zajišťuje spuštění i v restriktivnějších prohlížečích
    setTimeout(() => {
      window.print();
    }, 150);
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-12 font-sans text-slate-900">
      <style>{`
        @media print {
          @page {
            size: A4;
            margin: 0;
          }
          body {
            background: white !important;
            margin: 0 !important;
            padding: 0 !important;
            -webkit-print-color-adjust: exact !important;
            print-color-adjust: exact !important;
          }
          .no-print {
            display: none !important;
          }
          .print-container {
            width: 210mm !important;
            height: 297mm !important;
            padding: 15mm !important;
            margin: 0 auto !important;
            display: block !important;
            border: none !important;
            box-shadow: none !important;
          }
          .card-wrapper {
            display: flex !important;
            flex-direction: row !important;
            width: 100% !important;
            border: 1px solid #e2e8f0 !important;
            border-radius: 2rem !important;
            overflow: hidden !important;
          }
          .sidebar-print {
            background-color: #0f172a !important; /* slate-900 */
            color: white !important;
            width: 40% !important;
            padding: 2.5rem !important;
          }
          .content-print {
            width: 60% !important;
            padding: 2.5rem !important;
            background: white !important;
          }
        }
      `}</style>

      {/* Ovládací panel - schová se při tisku */}
      <header className="mb-12 flex flex-col items-center no-print">
        <div className="bg-blue-600 p-4 rounded-3xl shadow-xl mb-6">
          <Calculator className="w-10 h-10 text-white" />
        </div>
        <h1 className="text-4xl font-black mb-3 text-center">
          Služby - <span className="text-blue-600">denní plánování</span>
        </h1>
      </header>

      <main className="space-y-10">
        <section className="no-print">
          <div className={`relative border-4 border-dashed rounded-[2.5rem] p-12 text-center transition-colors
            ${fileName ? 'border-green-200 bg-green-50/30' : 'border-slate-200 bg-white'}`}>
            {/* Input je z-index 10 a jen pokud nemáme soubor, aby neblokoval zbytek stránky */}
            {!fileName && (
              <input
                type="file"
                accept=".xlsx, .xls"
                onChange={(e) => e.target.files?.[0] && processFile(e.target.files[0])}
                className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              />
            )}
            <div className="flex flex-col items-center gap-4">
              <div className={`p-4 rounded-full ${fileName ? 'bg-green-100 text-green-600' : 'bg-blue-50 text-blue-500'}`}>
                {fileName ? <CheckCircle2 className="w-8 h-8" /> : <FileText className="w-8 h-8" />}
              </div>
              <p className="text-xl font-bold">{fileName || 'Nahrajte Excel report'}</p>
              {fileName && (
                <button 
                  onClick={() => setFileName(null)} 
                  className="text-xs text-rose-500 font-bold uppercase tracking-widest hover:underline z-20"
                >
                  Změnit soubor
                </button>
              )}
            </div>
          </div>
        </section>

        {allCalculations.length > 0 && (
          <section className="max-w-2xl mx-auto no-print">
            <div className="bg-white rounded-3xl p-6 shadow-xl border border-slate-100 flex items-center gap-4">
              <div className="flex-grow">
                <select 
                  value={selectedBranch}
                  onChange={(e) => setSelectedBranch(e.target.value)}
                  className="w-full bg-slate-50 border-2 border-slate-100 rounded-2xl px-6 py-4 font-bold text-lg focus:outline-none"
                >
                  {availableBranches.map(b => <option key={b} value={b}>{b}</option>)}
                </select>
              </div>
              <button 
                onClick={handlePrint}
                className="bg-slate-900 hover:bg-black text-white rounded-2xl px-10 py-4 flex items-center gap-3 font-bold transition-transform active:scale-95 shadow-lg"
              >
                <Printer className="w-5 h-5" />
                <span>Tisk do PDF</span>
              </button>
            </div>
          </section>
        )}

        {error && <div className="p-6 bg-rose-50 border-2 border-rose-100 rounded-3xl text-rose-700 font-bold no-print">{error}</div>}

        {filteredResult && !isProcessing && (
          <div className="print-container">
            <div className="card-wrapper bg-white shadow-2xl border border-slate-100 rounded-[3rem] overflow-hidden flex flex-col lg:flex-row">
              {/* LEVÝ PANEL */}
              <div className="sidebar-print lg:w-2/5 bg-slate-900 p-12 text-white flex flex-col justify-between min-h-[400px]">
                <div>
                  <div className="flex items-center gap-3 mb-10 opacity-60">
                    <TrendingUp className="w-5 h-5" />
                    <span className="text-xs font-black uppercase tracking-widest">Denní plán - služby</span>
                  </div>
                  <h2 className="text-8xl font-black mb-2 tracking-tighter">
                    {Math.round(filteredResult.finalValue).toLocaleString('cs-CZ')}
                  </h2>
                  <p className="text-slate-400 text-xl mb-8">CZK / den</p>
                  <div className="inline-block px-4 py-2 bg-slate-800 rounded-xl border border-slate-700 font-bold">
                    {filteredResult.branchName}
                  </div>
                </div>
                <div className="mt-12 pt-8 border-t border-slate-800 text-sm text-slate-400">
                  Zbývá <strong className="text-white">{filteredResult.daysRemaining} dní</strong> do konce měsíce.
                </div>
              </div>

              {/* PRAVÝ PANEL */}
              <div className="content-print lg:w-3/5 p-12 space-y-8 bg-white">
                <div className="flex justify-between items-center no-print">
                  <h3 className="text-2xl font-bold">Načtená data</h3>
                  <button onClick={handlePrint} className="p-3 bg-slate-100 rounded-xl hover:bg-slate-200 transition-colors">
                    <Printer className="w-5 h-5 text-slate-600" />
                  </button>
                </div>
                <div className="print:block hidden text-xl font-bold mb-6">Načtená data</div>

                <div className="space-y-4">
                  <ValueRow label="Revenue RR" value={filteredResult.revenueRR} />
                  <ValueRow label="Plan ASR Services/Revenue" value={filteredResult.planAsrServicesRevenue} isPercent />
                  <ValueRow label="Service Asist Revenue" value={filteredResult.serviceAsistRevenue} isMinus />
                </div>

                <div className="mt-8 p-6 bg-slate-50 rounded-3xl border border-slate-100">
                  <div className="flex items-center gap-2 mb-3 text-slate-400">
                    <Info className="w-4 h-4" />
                    <span className="text-[10px] font-black uppercase tracking-widest">Kontrolní výpočet</span>
                  </div>
                  <div className="bg-white p-4 rounded-xl border border-slate-200 font-mono text-[11px] text-slate-400 break-all">
                    <code>(({filteredResult.revenueRR.toLocaleString('cs-CZ')} * {(filteredResult.planAsrServicesRevenue * 100).toFixed(2)}%) - {filteredResult.serviceAsistRevenue.toLocaleString('cs-CZ')}) / {filteredResult.daysRemaining}</code>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>

      <footer className="mt-20 py-8 border-t border-slate-100 text-center text-slate-300 text-[10px] font-black uppercase tracking-widest no-print">
        Export pro pobočku {selectedBranch} | {new Date().toLocaleDateString('cs-CZ')}
      </footer>
    </div>
  );
};

const ValueRow = ({ label, value, isPercent, isMinus }: any) => (
  <div className="flex items-center justify-between p-5 bg-white border border-slate-100 rounded-2xl shadow-sm">
    <span className="text-slate-400 font-bold text-sm">{label}</span>
    <div className="flex items-baseline gap-2">
      <span className={`text-2xl font-black ${isMinus ? 'text-rose-500' : 'text-slate-900'}`}>
        {isMinus && "− "}{isPercent ? (value * 100).toLocaleString('cs-CZ', { minimumFractionDigits: 2 }) : value.toLocaleString('cs-CZ')}
      </span>
      <span className="text-[10px] text-slate-300 font-black">{isPercent ? '%' : 'CZK'}</span>
    </div>
  </div>
);

