    }
  };

  const handleGeneratePDF = async () => {
    if (!resultsRef.current || !filteredResult) return;

    const element = resultsRef.current;
    const opt = {
      margin: 10,
      filename: `Plán_služeb_${filteredResult.branchName.replace(/\s+/g, '_')}.pdf`,
      image: { type: 'jpeg', quality: 0.98 },
      html2canvas: { scale: 2, useCORS: true },
      jsPDF: { unit: 'mm', format: 'a4', orientation: 'landscape' }
    };

    try {
      await window.html2pdf().set(opt).from(element).save();
    } catch (err) {
      console.error("Chyba při generování PDF:", err);
      alert("Nepodařilo se vygenerovat PDF. Zkuste to prosím znovu.");
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-12 font-sans text-slate-900">
      <header className="mb-12 flex flex-col items-center">
        <div className="bg-blue-600 p-4 rounded-3xl shadow-xl mb-6">
          <Calculator className="w-10 h-10 text-white" />
        </div>
        <h1 className="text-4xl font-black mb-3 text-center">
          Služby - <span className="text-blue-600">denní plánování</span>
        </h1>
        <p className="text-slate-400 font-medium">Nahrajte report a stáhněte si PDF s plánem</p>
      </header>

      <main className="space-y-10">
        <section>
          <div className={`relative border-4 border-dashed rounded-[2.5rem] p-12 text-center transition-colors
            ${fileName ? 'border-green-200 bg-green-50/30' : 'border-slate-200 bg-white hover:border-blue-300'}`}>
            {!fileName && (
              <input
                type="file"
                accept=".xlsx, .xls"
                onChange={(e) => e.target.files?.[0] && processFile(e.target.files[0])}
                className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              />
            )}
            <div className="flex flex-col items-center gap-4">
              <div className={`p-4 rounded-full ${fileName ? 'bg-green-100 text-green-600' : 'bg-blue-50 text-blue-500'}`}>
                {fileName ? <CheckCircle2 className="w-8 h-8" /> : <FileText className="w-8 h-8" />}
              </div>
              <p className="text-xl font-bold">{fileName || 'Nahrajte Excel report'}</p>
              {fileName && (
                <button 
                  onClick={() => setFileName(null)} 
                  className="text-xs text-rose-500 font-bold uppercase tracking-widest hover:underline relative z-20"
                >
                  Změnit soubor
                </button>
              )}
            </div>
          </div>
        </section>

        {allCalculations.length > 0 && (
          <section className="max-w-2xl mx-auto">
            <div className="bg-white rounded-3xl p-6 shadow-xl border border-slate-100 flex flex-col sm:flex-row items-center gap-4">
              <div className="flex-grow w-full">
                <label className="block text-[10px] font-black text-slate-400 uppercase tracking-widest mb-2 ml-1">Vyberte pobočku</label>
                <select 
                  value={selectedBranch}
                  onChange={(e) => setSelectedBranch(e.target.value)}
                  className="w-full bg-slate-50 border-2 border-slate-100 rounded-2xl px-6 py-4 font-bold text-lg focus:outline-none focus:border-blue-500 transition-colors"
                >
                  {availableBranches.map(b => <option key={b} value={b}>{b}</option>)}
                </select>
              </div>
              <button 
                onClick={handleGeneratePDF}
                className="w-full sm:w-auto mt-6 sm:mt-0 bg-blue-600 hover:bg-blue-700 text-white rounded-2xl px-10 py-5 flex items-center justify-center gap-3 font-bold transition-all active:scale-95 shadow-lg shadow-blue-200"
              >
                <Download className="w-5 h-5" />
                <span>Generovat PDF</span>
              </button>
            </div>
          </section>
        )}

        {error && <div className="p-6 bg-rose-50 border-2 border-rose-100 rounded-3xl text-rose-700 font-bold text-center">{error}</div>}

        {isProcessing && (
          <div className="flex flex-col items-center justify-center py-20 gap-4">
            <div className="w-12 h-12 border-4 border-slate-100 border-t-blue-600 rounded-full animate-spin"></div>
            <p className="text-slate-400 font-medium animate-pulse">Zpracovávám data...</p>
          </div>
        )}

        {filteredResult && !isProcessing && (
          <div className="animate-in fade-in zoom-in-95 duration-500">
            <div 
              ref={resultsRef}
              className="bg-white shadow-2xl border border-slate-100 rounded-[3rem] overflow-hidden flex flex-col md:flex-row min-h-[500px]"
            >
              {/* LEVÝ PANEL - TMAVÝ */}
              <div className="md:w-2/5 bg-slate-900 p-12 text-white flex flex-col justify-between">
                <div>
                  <div className="flex items-center gap-3 mb-12 opacity-60">
                    <TrendingUp className="w-5 h-5" />
                    <span className="text-xs font-black uppercase tracking-[0.2em]">Denní plán - služby</span>
                  </div>

                  <div className="mb-2">
                    <span className="text-8xl font-black tracking-tighter">
                      {Math.round(filteredResult.finalValue).toLocaleString('cs-CZ')}
                    </span>
                  </div>
                  <p className="text-slate-400 text-xl font-medium mb-10">CZK / den</p>

                  <div className="inline-flex items-center px-5 py-2.5 bg-slate-800 rounded-2xl border border-slate-700 font-bold text-lg shadow-sm">
                    {filteredResult.branchName}
                  </div>
                </div>

                <div className="mt-12 pt-10 border-t border-slate-800">
                  <div className="flex items-center gap-3 text-slate-400">
                    <Calendar className="w-5 h-5" />
                    <p className="text-sm font-medium">
                      Zbývá <strong className="text-white">{filteredResult.daysRemaining} dní</strong> do konce měsíce.
                    </p>
                  </div>
                </div>
              </div>

              {/* PRAVÝ PANEL - DATA */}
              <div className="md:w-3/5 p-12 space-y-10 bg-white">
                <div className="flex justify-between items-center">
                  <h3 className="text-3xl font-black text-slate-900 tracking-tight">Načtená data</h3>
                  <Download className="w-6 h-6 text-slate-100" />
                </div>

                <div className="space-y-4">
                  <ValueRow label="Revenue RR" value={filteredResult.revenueRR} />
                  <ValueRow label="Plan ASR Services/Revenue" value={filteredResult.planAsrServicesRevenue} isPercent />
                  <ValueRow label="Service Asist Revenue" value={filteredResult.serviceAsistRevenue} isMinus />
                </div>

                <div className="mt-10 p-8 bg-slate-50 rounded-[2.5rem] border border-slate-100">
                  <div className="flex items-center gap-3 mb-4 text-slate-400">
                    <Info className="w-4 h-4" />
                    <span className="text-[10px] font-black uppercase tracking-widest">Kontrolní výpočet</span>
                  </div>
                  <div className="bg-white p-6 rounded-2xl border border-slate-200 font-mono text-[11px] text-slate-400 break-all leading-relaxed shadow-sm">
                    <code>(({filteredResult.revenueRR.toLocaleString('cs-CZ')} * {(filteredResult.planAsrServicesRevenue * 100).toFixed(2)}%) - {filteredResult.serviceAsistRevenue.toLocaleString('cs-CZ')}) / {filteredResult.daysRemaining}</code>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>

      <footer className="mt-20 py-8 border-t border-slate-100 text-center text-slate-300 text-[10px] font-black uppercase tracking-widest">
        Plánovač služeb v1.2 | {new Date().toLocaleDateString('cs-CZ')}
      </footer>
    </div>
  );
};

const ValueRow = ({ label, value, isPercent, isMinus }: any) => {
  const formattedVal = isPercent 
    ? (value * 100).toLocaleString('cs-CZ', { minimumFractionDigits: 2, maximumFractionDigits: 2 })
    : value.toLocaleString('cs-CZ');

  return (
    <div className="flex items-center justify-between p-6 bg-white border border-slate-100 rounded-3xl shadow-sm hover:border-blue-100 transition-colors">
      <span className="text-slate-400 font-bold text-sm tracking-wide">{label}</span>
      <div className="flex items-baseline gap-2">
        <span className={`text-2xl font-black ${isMinus ? 'text-rose-500' : 'text-slate-900'}`}>
          {isMinus && "− "}{formattedVal}
        </span>
        <span className="text-[10px] text-slate-300 font-black uppercase">{isPercent ? '%' : 'CZK'}</span>
      </div>
    </div>
  );
};

export default App;

import React, { useState, useMemo, useRef } from 'react';
import * as XLSX from 'xlsx';
import { 
  Calculator, 
  AlertCircle, 
  CheckCircle2, 
  Info, 
  TrendingUp, 
  Calendar, 
  FileText,
  ChevronDown,
  Download,
  FileDown
} from 'lucide-react';
import { CalculationResult } from './types';
import { getDaysRemainingInMonth } from './utils/dateUtils';

// Definice typu pro globální window objekt s knihovnou html2pdf
declare global {
  interface Window {
    html2pdf: any;
  }
}

const App: React.FC = () => {
  const [allCalculations, setAllCalculations] = useState<CalculationResult[]>([]);
  const [selectedBranch, setSelectedBranch] = useState<string>("");
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [fileName, setFileName] = useState<string | null>(null);
  const resultsRef = useRef<HTMLDivElement>(null);

  const availableBranches = useMemo(() => {
    return Array.from(new Set(allCalculations.map(c => c.branchName))).sort();
  }, [allCalculations]);

  const filteredResult = useMemo(() => {
    if (!selectedBranch) return null;
    return allCalculations.find(c => c.branchName === selectedBranch) || null;
  }, [allCalculations, selectedBranch]);

  const processFile = async (file: File) => {
    setIsProcessing(true);
    setError(null);
    setFileName(file.name);
    setAllCalculations([]);
    setSelectedBranch("");

    try {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const ab = e.target?.result;
          const wb = XLSX.read(ab, { type: 'array' });

          const targetSheetName = "Branch Performance";
          let ws = wb.Sheets[targetSheetName];
          if (!ws) ws = wb.Sheets[wb.SheetNames[0]];

          const rows: any[][] = XLSX.utils.sheet_to_json(ws, { header: 1 });

          if (rows.length < 5) {
            throw new Error("Soubor je příliš krátký. Záhlaví musí být na 5. řádku.");
          }

          const headerRow = rows[4]; // 5. řádek (index 4)

          const findColIndex = (names: string[]) => {
            return headerRow.findIndex(cell => 
              cell && names.some(name => String(cell).toLowerCase().trim() === name.toLowerCase().trim())
            );
          };

          const colIdxBranch = findColIndex(['BranchName', 'Pobočka']);
          const colIdxRevRR = findColIndex(['Revenue RR']);
          const colIdxServAsist = findColIndex(['Service Asist Revenue']);
          const colIdxPlanAsr = findColIndex(['Plan ASR Services/Revenue']);

          if (colIdxBranch === -1 || colIdxRevRR === -1 || colIdxServAsist === -1 || colIdxPlanAsr === -1) {
            setError("Nepodařilo se najít všechny sloupce na 5. řádku. Zkontrolujte názvy.");
            setIsProcessing(false);
            return;
          }

          const daysRemaining = getDaysRemainingInMonth();
          const results: CalculationResult[] = [];

          for (let i = 5; i < rows.length; i++) {
            const row = rows[i];
            const branchVal = row[colIdxBranch]?.toString().trim();

            if (branchVal) {
              const parseVal = (val: any) => {
                if (typeof val === 'number') return val;
                if (!val) return 0;
                const cleaned = String(val).replace(/\s/g, '').replace(',', '.');
                const num = parseFloat(cleaned);
                return isNaN(num) ? 0 : num;
              };

              const revRR = parseVal(row[colIdxRevRR]);
              const planAsr = parseVal(row[colIdxPlanAsr]);
              const serAsist = parseVal(row[colIdxServAsist]);

              if (revRR === 0 && planAsr === 0) continue;

              const totalRevenue = (revRR * planAsr) - serAsist;
              const finalValue = totalRevenue / daysRemaining;

              results.push({
                branchName: branchVal,
                revenueRR: revRR,
                planAsrServicesRevenue: planAsr,
                serviceAsistRevenue: serAsist,
                daysRemaining,
                finalValue,
                rawRow: row
              });
            }
          }

          setAllCalculations(results);
          const budejovicka = results.find(r => r.branchName.toLowerCase().includes("budějovická"));
          setSelectedBranch(budejovicka ? budejovicka.branchName : (results[0]?.branchName || ""));

        } catch (err: any) {
          setError(err.message);
        } finally {
          setIsProcessing(false);
        }
      };
      reader.readAsArrayBuffer(file);
    } catch (err) {
      setError("Chyba při nahrávání.");
      setIsProcessing(false);
    }
  };

  const handleGeneratePDF = async () => {
    if (!resultsRef.current || !filteredResult) return;

    const element = resultsRef.current;

    // Optimalizovaná konfigurace pro A4 na výšku
    const opt = {
      margin: 15,
      filename: `Report_Služeb_${filteredResult.branchName.replace(/\s+/g, '_')}_${new Date().toISOString().split('T')[0]}.pdf`,
      image: { type: 'jpeg', quality: 1.0 },
      html2canvas: { 
        scale: 3, 
        useCORS: true, 
        letterRendering: true,
        logging: false
      },
      jsPDF: { unit: 'mm', format: 'a4', orientation: 'portrait' }
    };

    try {
      // Před generováním můžeme dočasně upravit styl pro PDF
      const originalStyle = element.style.cssText;
      element.style.width = '210mm'; // Vynutit šířku A4

      await window.html2pdf().set(opt).from(element).save();

      element.style.cssText = originalStyle; // Vrátit původní styl
    } catch (err) {
      console.error("Chyba při generování PDF:", err);
      alert("Nepodařilo se vygenerovat PDF. Zkuste to prosím znovu.");
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-12 font-sans text-slate-900">
      <header className="mb-12 flex flex-col items-center">
        <div className="bg-blue-600 p-4 rounded-3xl shadow-xl mb-6">
          <Calculator className="w-10 h-10 text-white" />
        </div>
        <h1 className="text-4xl font-black mb-3 text-center">
          Služby - <span className="text-blue-600">denní plánování</span>
        </h1>
        <p className="text-slate-400 font-medium">Excel analýza & generování PDF reportu (A4)</p>
      </header>

      <main className="space-y-10">
        {/* Nahrávání souboru */}
        <section>
          <div className={`relative border-4 border-dashed rounded-[2.5rem] p-12 text-center transition-all duration-300
            ${fileName ? 'border-green-200 bg-green-50/30' : 'border-slate-200 bg-white hover:border-blue-300 shadow-sm hover:shadow-md'}`}>
            {!fileName && (
              <input
                type="file"
                accept=".xlsx, .xls"
                onChange={(e) => e.target.files?.[0] && processFile(e.target.files[0])}
                className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              />
            )}
            <div className="flex flex-col items-center gap-4">
              <div className={`p-4 rounded-full ${fileName ? 'bg-green-100 text-green-600' : 'bg-blue-50 text-blue-500'}`}>
                {fileName ? <CheckCircle2 className="w-8 h-8" /> : <FileText className="w-8 h-8" />}
              </div>
              <div>
                <p className="text-xl font-bold text-slate-800">{fileName || 'Vyberte Excel soubor'}</p>
                {!fileName && <p className="text-slate-400 text-sm mt-1">Podporuje .xlsx a .xls (Data od 5. řádku)</p>}
              </div>
              {fileName && (
                <button 
                  onClick={() => setFileName(null)} 
                  className="text-xs text-rose-500 font-black uppercase tracking-widest hover:text-rose-600 transition-colors relative z-20 mt-2"
                >
                  Nahrát jiný soubor
                </button>
              )}
            </div>
          </div>
        </section>

        {/* Volba pobočky a PDF tlačítko */}
        {allCalculations.length > 0 && (
          <section className="max-w-3xl mx-auto">
            <div className="bg-white rounded-[2.5rem] p-6 shadow-xl border border-slate-100 flex flex-col sm:flex-row items-end gap-4">
              <div className="flex-grow w-full">
                <label className="block text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mb-3 ml-1">Zvolená pobočka</label>
                <div className="relative">
                  <select 
                    value={selectedBranch}
                    onChange={(e) => setSelectedBranch(e.target.value)}
                    className="w-full appearance-none bg-slate-50 border-2 border-slate-100 rounded-2xl px-6 py-4 font-bold text-lg focus:outline-none focus:border-blue-500 transition-all cursor-pointer"
                  >
                    {availableBranches.map(b => <option key={b} value={b}>{b}</option>)}
                  </select>
                  <div className="absolute right-6 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400">
                    <ChevronDown className="w-5 h-5" />
                  </div>
                </div>
              </div>
              <button 
                onClick={handleGeneratePDF}
                className="w-full sm:w-auto bg-slate-900 hover:bg-black text-white rounded-2xl px-10 py-5 flex items-center justify-center gap-3 font-bold transition-all active:scale-95 shadow-lg shadow-slate-200 group"
              >
                <FileDown className="w-5 h-5 group-hover:translate-y-0.5 transition-transform" />
                <span>Uložit jako PDF (A4)</span>
              </button>
            </div>
          </section>
        )}

        {error && <div className="p-8 bg-rose-50 border-2 border-rose-100 rounded-3xl text-rose-700 font-bold text-center animate-pulse">{error}</div>}

        {isProcessing && (
          <div className="flex flex-col items-center justify-center py-24 gap-6">
            <div className="relative w-20 h-20">
              <div className="absolute inset-0 border-4 border-slate-100 rounded-full"></div>
              <div className="absolute inset-0 border-4 border-t-blue-600 rounded-full animate-spin"></div>
            </div>
            <p className="text-slate-400 font-bold tracking-widest uppercase text-xs animate-pulse">Analyzuji report...</p>
          </div>
        )}

        {/* Náhled reportu / PDF zdroj */}
        {filteredResult && !isProcessing && (
          <div className="animate-in fade-in zoom-in-95 duration-700">
            <div 
              ref={resultsRef}
              className="bg-white shadow-2xl border border-slate-100 rounded-[3.5rem] overflow-hidden flex flex-col md:flex-row min-h-[600px] pdf-content"
            >
              {/* LEVÝ PANEL - TMAVÝ (Sidebar) */}
              <div className="md:w-[40%] bg-slate-900 p-12 text-white flex flex-col justify-between">
                <div>
                  <div className="flex items-center gap-3 mb-16 opacity-50">
                    <TrendingUp className="w-6 h-6" />
                    <span className="text-xs font-black uppercase tracking-[0.3em]">Denní plán - služby</span>
                  </div>

                  <div className="mb-4">
                    <span className="text-8xl md:text-9xl font-black tracking-tighter block mb-2">
                      {Math.round(filteredResult.finalValue).toLocaleString('cs-CZ')}
                    </span>
                    <p className="text-slate-400 text-2xl font-medium">CZK / den</p>
                  </div>

                  <div className="mt-12 inline-flex items-center px-6 py-3 bg-slate-800 rounded-2xl border border-slate-700 font-bold text-xl shadow-lg">
                    {filteredResult.branchName}
                  </div>
                </div>

                <div className="mt-20 pt-10 border-t border-slate-800 flex flex-col gap-2">
                  <div className="flex items-center gap-3 text-slate-400">
                    <Calendar className="w-5 h-5 text-blue-500" />
                    <p className="text-sm font-bold">
                      Zbývá <strong className="text-white">{filteredResult.daysRemaining} dní</strong> v tomto měsíci.
                    </p>
                  </div>
                  <p className="text-[10px] text-slate-500 font-bold uppercase tracking-widest mt-4">
                    Vygenerováno: {new Date().toLocaleDateString('cs-CZ')} v {new Date().toLocaleTimeString('cs-CZ', { hour: '2-digit', minute: '2-digit' })}
                  </p>
                </div>
              </div>

              {/* PRAVÝ PANEL - DATA (Content) */}
              <div className="md:w-[60%] p-12 lg:p-16 space-y-10 bg-white flex flex-col justify-center">
                <div className="space-y-2">
                  <h3 className="text-4xl font-black text-slate-900 tracking-tight">Klíčové metriky</h3>
                  <p className="text-slate-400 font-medium">Přehled vstupních hodnot načtených z reportu</p>
                </div>

                <div className="space-y-5">
                  <ValueRow label="Revenue RR" value={filteredResult.revenueRR} />
                  <ValueRow label="Plan ASR Services/Revenue" value={filteredResult.planAsrServicesRevenue} isPercent />
                  <ValueRow label="Service Asist Revenue" value={filteredResult.serviceAsistRevenue} isMinus />
                </div>

                <div className="mt-12 p-10 bg-slate-50 rounded-[3rem] border border-slate-100 relative overflow-hidden group">
                  <div className="absolute top-0 right-0 p-4 opacity-5 group-hover:opacity-10 transition-opacity">
                    <Calculator className="w-24 h-24" />
                  </div>
                  <div className="flex items-center gap-3 mb-6 text-slate-400 relative z-10">
                    <Info className="w-5 h-5 text-blue-400" />
                    <span className="text-xs font-black uppercase tracking-[0.2em]">Metodika výpočtu</span>
                  </div>
                  <div className="bg-white p-6 rounded-2xl border border-slate-200 font-mono text-[12px] text-slate-400 break-all leading-relaxed shadow-sm relative z-10">
                    <span className="text-blue-500 font-bold">// ((RevRR * PlanASR%) - ServAsist) / Dny</span>
                    <div className="mt-2 text-slate-500">
                      <code>(({filteredResult.revenueRR.toLocaleString('cs-CZ')} * {(filteredResult.planAsrServicesRevenue * 100).toFixed(2)}%) - {filteredResult.serviceAsistRevenue.toLocaleString('cs-CZ')}) / {filteredResult.daysRemaining}</code>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>

      <footer className="mt-24 py-10 border-t border-slate-100 flex flex-col items-center gap-4 text-slate-300 text-[10px] font-black uppercase tracking-[0.3em]">
        <div className="flex items-center gap-2">
          <TrendingUp className="w-4 h-4" />
          <span>Branch Performance Insights 2024</span>
        </div>
      </footer>
    </div>
  );
};

const ValueRow = ({ label, value, isPercent, isMinus }: any) => {
  const formattedVal = isPercent 
    ? (value * 100).toLocaleString('cs-CZ', { minimumFractionDigits: 2, maximumFractionDigits: 2 })
    : value.toLocaleString('cs-CZ');

  return (
    <div className="flex items-center justify-between p-7 bg-white border border-slate-100 rounded-3xl shadow-sm hover:border-blue-100 hover:shadow-md transition-all">
      <span className="text-slate-400 font-bold text-sm tracking-wide uppercase">{label}</span>
      <div className="flex items-baseline gap-2">
        <span className={`text-3xl font-black ${isMinus ? 'text-rose-500' : 'text-slate-900'}`}>
          {isMinus && "− "}{formattedVal}
        </span>
        <span className="text-xs text-slate-300 font-black uppercase">{isPercent ? '%' : 'CZK'}</span>
      </div>
    </div>
  );
};

export default App;

import React, { useState, useMemo, useRef } from 'react';
import * as XLSX from 'xlsx';
import { 
  Calculator, 
  AlertCircle, 
  CheckCircle2, 
  Info, 
  TrendingUp, 
  Calendar, 
  FileText,
  ChevronDown,
  Download,
  FileDown
} from 'lucide-react';
import { CalculationResult } from './types';
import { getDaysRemainingInMonth } from './utils/dateUtils';

// Definice typu pro globální window objekt s knihovnou html2pdf
declare global {
  interface Window {
    html2pdf: any;
  }
}

const App: React.FC = () => {
  const [allCalculations, setAllCalculations] = useState<CalculationResult[]>([]);
  const [selectedBranch, setSelectedBranch] = useState<string>("");
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [fileName, setFileName] = useState<string | null>(null);
  const resultsRef = useRef<HTMLDivElement>(null);

  const availableBranches = useMemo(() => {
    return Array.from(new Set(allCalculations.map(c => c.branchName))).sort();
  }, [allCalculations]);

  const filteredResult = useMemo(() => {
    if (!selectedBranch) return null;
    return allCalculations.find(c => c.branchName === selectedBranch) || null;
  }, [allCalculations, selectedBranch]);

  const processFile = async (file: File) => {
    setIsProcessing(true);
    setError(null);
    setFileName(file.name);
    setAllCalculations([]);
    setSelectedBranch("");

    try {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const ab = e.target?.result;
          const wb = XLSX.read(ab, { type: 'array' });

          const targetSheetName = "Branch Performance";
          let ws = wb.Sheets[targetSheetName];
          if (!ws) ws = wb.Sheets[wb.SheetNames[0]];

          const rows: any[][] = XLSX.utils.sheet_to_json(ws, { header: 1 });

          if (rows.length < 5) {
            throw new Error("Soubor je příliš krátký. Záhlaví musí být na 5. řádku.");
          }

          const headerRow = rows[4]; // 5. řádek (index 4)

          const findColIndex = (names: string[]) => {
            return headerRow.findIndex(cell => 
              cell && names.some(name => String(cell).toLowerCase().trim() === name.toLowerCase().trim())
            );
          };

          const colIdxBranch = findColIndex(['BranchName', 'Pobočka']);
          const colIdxRevRR = findColIndex(['Revenue RR']);
          const colIdxServAsist = findColIndex(['Service Asist Revenue']);
          const colIdxPlanAsr = findColIndex(['Plan ASR Services/Revenue']);

          if (colIdxBranch === -1 || colIdxRevRR === -1 || colIdxServAsist === -1 || colIdxPlanAsr === -1) {
            setError("Nepodařilo se najít všechny sloupce na 5. řádku. Zkontrolujte názvy.");
            setIsProcessing(false);
            return;
          }

          const daysRemaining = getDaysRemainingInMonth();
          const results: CalculationResult[] = [];

          for (let i = 5; i < rows.length; i++) {
            const row = rows[i];
            const branchVal = row[colIdxBranch]?.toString().trim();

            if (branchVal) {
              const parseVal = (val: any) => {
                if (typeof val === 'number') return val;
                if (!val) return 0;
                const cleaned = String(val).replace(/\s/g, '').replace(',', '.');
                const num = parseFloat(cleaned);
                return isNaN(num) ? 0 : num;
              };

              const revRR = parseVal(row[colIdxRevRR]);
              const planAsr = parseVal(row[colIdxPlanAsr]);
              const serAsist = parseVal(row[colIdxServAsist]);

              if (revRR === 0 && planAsr === 0) continue;

              const totalRevenue = (revRR * planAsr) - serAsist;
              const finalValue = totalRevenue / daysRemaining;

              results.push({
                branchName: branchVal,
                revenueRR: revRR,
                planAsrServicesRevenue: planAsr,
                serviceAsistRevenue: serAsist,
                daysRemaining,
                finalValue,
                rawRow: row
              });
            }
          }

          setAllCalculations(results);
          const budejovicka = results.find(r => r.branchName.toLowerCase().includes("budějovická"));
          setSelectedBranch(budejovicka ? budejovicka.branchName : (results[0]?.branchName || ""));

        } catch (err: any) {
          setError(err.message);
        } finally {
          setIsProcessing(false);
        }
      };
      reader.readAsArrayBuffer(file);
    } catch (err) {
      setError("Chyba při nahrávání.");
      setIsProcessing(false);
    }
  };

