const ValueRow = ({ label, value, isPercent, isMinus }: any) => {
  const formattedVal = isPercent 
    ? (value * 100).toLocaleString('cs-CZ', { minimumFractionDigits: 1, maximumFractionDigits: 1 })
    : value.toLocaleString('cs-CZ');

  return (
    <div className="flex items-center justify-between p-4 lg:p-7 bg-white border border-slate-100 rounded-3xl shadow-sm hover:border-blue-100 transition-all">
