                <div className="space-y-3 lg:space-y-5">
                  <ValueRow label="Revenue RR" value={filteredResult.revenueRR} />
                  <ValueRow label="Plan ASR Services" value={filteredResult.planAsrServicesRevenue} isPercent truncate />
                  <ValueRow label="Service Asist" value={filteredResult.serviceAsistRevenue} isMinus />
                </div>

                <div className="mt-6 lg:mt-12 p-8 lg:p-10 bg-slate-50 rounded-[3rem] border border-slate-100 relative overflow-hidden group">
                  <div className="absolute top-0 right-0 p-4 opacity-5 group-hover:opacity-10 transition-opacity">
                    <Calculator className="w-16 lg:w-24 h-16 lg:h-24" />
