};

const ValueRow = ({ label, value, isPercent, isMinus, truncate }: any) => {
  let formattedVal: string;

  if (isPercent) {
    const percentVal = value * 100;
    if (truncate) {
      const truncated = Math.floor(percentVal * 100) / 100;
      formattedVal = truncated.toLocaleString('cs-CZ', { 
        minimumFractionDigits: 2, 
        maximumFractionDigits: 2 
      });
    } else {
      formattedVal = percentVal.toLocaleString('cs-CZ', { 
        minimumFractionDigits: 1, 
        maximumFractionDigits: 1 
      });
    }
  } else {
    formattedVal = Math.round(value).toLocaleString('cs-CZ');
  }

  return (
    <div className="flex items-center justify-between p-4 lg:p-7 bg-white border border-slate-100 rounded-3xl shadow-sm hover:border-blue-100 transition-all">
      <span className="text-slate-400 font-bold text-[10px] lg:text-sm tracking-wide uppercase shrink-0 mr-4">{label}</span>
      <div className="flex items-baseline gap-2 shrink-0">
        <span className={`text-2xl lg:text-3xl font-black ${isMinus ? 'text-rose-500' : 'text-slate-900'}`}>
          {isMinus && "− "}{formattedVal}
        </span>
        <span className="text-[10px] lg:text-xs text-slate-300 font-black uppercase">{isPercent ? '%' : 'CZK'}</span>
      </div>
    </div>
  );
};

export default App;

import React, { useState, useMemo, useRef, useEffect } from 'react';
import * as XLSX from 'xlsx';
import { 
  Calculator, 
  CheckCircle2, 
  Info, 
  TrendingUp, 
  Calendar, 
  FileText, 
  ChevronDown, 
  FileDown,
  Download
} from 'lucide-react';
import { CalculationResult } from './types';
import { getDaysRemainingInMonth } from './utils/dateUtils';

// Definice typu pro globální window objekt s knihovnou html2pdf
declare global {
  interface Window {
    html2pdf: any;
  }
}

const App: React.FC = () => {
  const [allCalculations, setAllCalculations] = useState<CalculationResult[]>([]);
  const [selectedBranch, setSelectedBranch] = useState<string>("");
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [fileName, setFileName] = useState<string | null>(null);
  const [showInstallInfo, setShowInstallInfo] = useState(true);
  const resultsRef = useRef<HTMLDivElement>(null);

  // Automatické odscrollování k výsledkům po zpracování
  useEffect(() => {
    if (allCalculations.length > 0 && resultsRef.current && !isProcessing) {
      resultsRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }, [allCalculations, isProcessing]);

  const availableBranches = useMemo(() => {
    return Array.from(new Set(allCalculations.map(c => c.branchName))).sort();
  }, [allCalculations]);

  const filteredResult = useMemo(() => {
    if (!selectedBranch) return null;
    return allCalculations.find(c => c.branchName === selectedBranch) || null;
  }, [allCalculations, selectedBranch]);

  const processFile = async (file: File) => {
    setIsProcessing(true);
    setError(null);
    setFileName(file.name);
    setAllCalculations([]);
    setSelectedBranch("");

    try {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const ab = e.target?.result;
          const wb = XLSX.read(ab, { type: 'array' });

          const targetSheetName = "Branch Performance";
          let ws = wb.Sheets[targetSheetName];
          if (!ws) ws = wb.Sheets[wb.SheetNames[0]];

          const rows: any[][] = XLSX.utils.sheet_to_json(ws, { header: 1 });

          if (rows.length < 5) {
            throw new Error("Soubor je příliš krátký. Záhlaví musí být na 5. řádku.");
          }

          const headerRow = rows[4];
          const findColIndex = (names: string[]) => {
            return headerRow.findIndex(cell => 
              cell && names.some(name => String(cell).toLowerCase().trim() === name.toLowerCase().trim())
            );
          };

          const colIdxBranch = findColIndex(['BranchName', 'Pobočka']);
          const colIdxRevRR = findColIndex(['Revenue RR']);
          const colIdxServAsist = findColIndex(['Service Asist Revenue']);
          const colIdxPlanAsr = findColIndex(['Plan ASR Services/Revenue']);

          if (colIdxBranch === -1 || colIdxRevRR === -1 || colIdxServAsist === -1 || colIdxPlanAsr === -1) {
            setError("Nepodařilo se najít všechny sloupce. Zkontrolujte názvy v Excelu na 5. řádku.");
            setIsProcessing(false);
            return;
          }

          const daysRemaining = getDaysRemainingInMonth();
          const results: CalculationResult[] = [];

          for (let i = 5; i < rows.length; i++) {
            const row = rows[i];
            const branchVal = row[colIdxBranch]?.toString().trim();

            if (branchVal) {
              const parseVal = (val: any) => {
                if (typeof val === 'number') return val;
                if (!val) return 0;
                const cleaned = String(val).replace(/\s/g, '').replace(',', '.');
                const num = parseFloat(cleaned);
                return isNaN(num) ? 0 : num;
              };

              const revRR = Math.round(parseVal(row[colIdxRevRR]));
              const planAsr = parseVal(row[colIdxPlanAsr]);
              const serAsist = Math.round(parseVal(row[colIdxServAsist]));

              if (revRR === 0 && planAsr === 0) continue;

              const totalRevenue = (revRR * planAsr) - serAsist;
              const finalValue = totalRevenue / daysRemaining;

              results.push({
                branchName: branchVal,
                revenueRR: revRR,
                planAsrServicesRevenue: planAsr,
                serviceAsistRevenue: serAsist,
                daysRemaining,
                finalValue,
                rawRow: row
              });
            }
          }

          setAllCalculations(results);
          const budejovicka = results.find(r => r.branchName.toLowerCase().includes("budějovická"));
          setSelectedBranch(budejovicka ? budejovicka.branchName : (results[0]?.branchName || ""));

        } catch (err: any) {
          setError(err.message);
        } finally {
          setIsProcessing(false);
        }
      };
      reader.readAsArrayBuffer(file);
    } catch (err) {
      setError("Chyba při nahrávání.");
      setIsProcessing(false);
    }
  };

  const handleGeneratePDF = async () => {
    if (!resultsRef.current || !filteredResult) return;

    const element = resultsRef.current;
    const parent = element.parentElement;
    if (!parent) return;

    const opt = {
      margin: 0,
      filename: `Report_Služeb_${filteredResult.branchName.replace(/\s+/g, '_')}.pdf`,
      image: { type: 'jpeg', quality: 1.0 },
      html2canvas: { 
        scale: 3, 
        useCORS: true, 
        letterRendering: true,
        backgroundColor: '#ffffff',
        logging: false,
        y: 0,
        scrollX: 0,
        scrollY: 0,
        windowWidth: 1200
      },
      jsPDF: { unit: 'mm', format: 'a4', orientation: 'landscape' }
    };

    try {
      const originalElementStyle = element.style.cssText;
      const originalParentStyle = parent.style.cssText;

      parent.style.width = '297mm';
      parent.style.height = '210mm';
      parent.style.display = 'flex';
      parent.style.flexDirection = 'column';
      parent.style.alignItems = 'center';
      parent.style.justifyContent = 'center';
      parent.style.padding = '0';
      parent.style.backgroundColor = '#ffffff';
      parent.style.margin = '0';
      parent.style.position = 'relative';

      element.style.width = '275mm';
      element.style.height = '180mm';
      element.style.minHeight = '180mm';
      element.style.margin = '0';
      element.style.boxShadow = 'none';
      element.style.border = '1px solid #f1f5f9';
      element.style.borderRadius = '2.5rem';

      await window.html2pdf().set(opt).from(parent).save();

      element.style.cssText = originalElementStyle;
      parent.style.cssText = originalParentStyle;
    } catch (err) {
      console.error("Chyba při generování PDF:", err);
      alert("Chyba při generování PDF.");
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-8 md:py-12 font-sans text-slate-900">
      {/* PWA Install Info */}
      {showInstallInfo && (
        <div className="mb-8 bg-blue-50 border border-blue-100 rounded-3xl p-5 flex flex-col sm:flex-row items-center gap-4 animate-in slide-in-from-top duration-500" data-html2canvas-ignore="true">
          <div className="bg-blue-600 p-3 rounded-2xl text-white">
            <Download className="w-5 h-5" />
          </div>
          <div className="flex-grow text-center sm:text-left">
            <h4 className="font-black text-blue-900 text-sm">Chcete aplikaci na plochu?</h4>
            <p className="text-blue-600/70 text-xs font-medium">Klikněte na ikonu instalace v adresním řádku prohlížeče (vpravo nahoře).</p>
          </div>
          <button 
            onClick={() => setShowInstallInfo(false)}
            className="text-[10px] font-black uppercase tracking-widest text-blue-400 hover:text-blue-600 transition-colors px-4 py-2"
          >
            Zavřít
          </button>
        </div>
      )}

      <header className="mb-12 flex flex-col items-center" data-html2canvas-ignore="true">
        <div className="bg-blue-600 p-4 rounded-3xl shadow-xl mb-6">
          <Calculator className="w-10 h-10 text-white" />
        </div>
        <h1 className="text-3xl md:text-4xl font-black mb-3 text-center">
          Služby - <span className="text-blue-600">denní plánování</span>
        </h1>
        <p className="text-slate-400 font-medium text-center">Exportovatelný nástroj pro pobočky</p>
      </header>

      <main className="space-y-10">
        <section data-html2canvas-ignore="true">
          <div className={`relative border-4 border-dashed rounded-[2.5rem] p-12 text-center transition-all duration-300
            ${fileName ? 'border-green-200 bg-green-50/30' : 'border-slate-200 bg-white hover:border-blue-300 shadow-sm hover:shadow-md'}`}>
            {!fileName && (
              <input
                type="file"
                accept=".xlsx, .xls"
                onChange={(e) => e.target.files?.[0] && processFile(e.target.files[0])}
                className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              />
            )}
            <div className="flex flex-col items-center gap-4">
              <div className={`p-4 rounded-full ${fileName ? 'bg-green-100 text-green-600' : 'bg-blue-50 text-blue-500'}`}>
                {fileName ? <CheckCircle2 className="w-8 h-8" /> : <FileText className="w-8 h-8" />}
              </div>
              <div>
                <p className="text-xl font-bold text-slate-800">{fileName || 'Vyber aktuální Denní Hlášení'}</p>
              </div>
              {fileName && (
                <button 
                  onClick={() => setFileName(null)} 
                  className="text-xs text-rose-500 font-black uppercase tracking-widest hover:text-rose-600 transition-colors relative z-20 mt-2"
                >
                  Nahrát jiný soubor
                </button>
              )}
            </div>
          </div>
        </section>

        {allCalculations.length > 0 && (
          <section className="max-w-3xl mx-auto" data-html2canvas-ignore="true">
            <div className="bg-white rounded-[2.5rem] p-6 shadow-xl border border-slate-100 flex flex-col sm:flex-row items-end gap-4">
              <div className="flex-grow w-full">
                <label className="block text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mb-3 ml-1">Zvolená pobočka</label>
                <div className="relative">
                  <select 
                    value={selectedBranch}
                    onChange={(e) => setSelectedBranch(e.target.value)}
                    className="w-full appearance-none bg-slate-50 border-2 border-slate-100 rounded-2xl px-6 py-4 font-bold text-lg focus:outline-none focus:border-blue-500 transition-all cursor-pointer"
                  >
                    {availableBranches.map(b => <option key={b} value={b}>{b}</option>)}
                  </select>
                  <div className="absolute right-6 top-1/2 -translate-y-1/2 pointer-events-none text-slate-400">
                    <ChevronDown className="w-5 h-5" />
                  </div>
                </div>
              </div>
              <button 
                onClick={handleGeneratePDF}
                className="w-full sm:w-auto bg-slate-900 hover:bg-black text-white rounded-2xl px-10 py-5 flex items-center justify-center gap-3 font-bold transition-all active:scale-95 shadow-lg shadow-slate-200 group"
              >
                <FileDown className="w-5 h-5 group-hover:translate-y-0.5 transition-transform" />
                <span>Uložit PDF</span>
              </button>
            </div>
          </section>
        )}

        {error && <div className="p-8 bg-rose-50 border-2 border-rose-100 rounded-3xl text-rose-700 font-bold text-center animate-pulse">{error}</div>}

        {isProcessing && (
          <div className="flex flex-col items-center justify-center py-24 gap-6">
            <div className="relative w-20 h-20">
              <div className="absolute inset-0 border-4 border-slate-100 rounded-full"></div>
              <div className="absolute inset-0 border-4 border-t-blue-600 rounded-full animate-spin"></div>
            </div>
            <p className="text-slate-400 font-bold tracking-widest uppercase text-xs animate-pulse text-center">Analyzuji report...</p>
          </div>
        )}

        {filteredResult && !isProcessing && (
          <div className="animate-in fade-in zoom-in-95 duration-700 flex justify-center pt-4 overflow-visible">
            <div 
              ref={resultsRef}
              className="bg-white shadow-2xl border border-slate-100 rounded-[3.5rem] overflow-hidden flex flex-col md:flex-row min-h-[600px] w-full max-w-5xl mx-auto"
            >
              <div className="md:w-[40%] bg-slate-900 p-10 lg:p-12 text-white flex flex-col justify-between shrink-0">
                <div>
                  <div className="flex items-center gap-3 mb-10 lg:mb-16 opacity-50">
                    <TrendingUp className="w-6 h-6" />
                    <span className="text-xs font-black uppercase tracking-[0.3em]">Denní plán - služby</span>
                  </div>

                  <div className="mb-4">
                    <span className="text-5xl md:text-6xl lg:text-7xl font-black tracking-tighter block mb-2 leading-none">
                      {Math.round(filteredResult.finalValue).toLocaleString('cs-CZ')}
                    </span>
                    <p className="text-slate-400 text-lg lg:text-xl font-medium">CZK / den</p>
                  </div>

                  <div className="mt-8 lg:mt-10 inline-flex items-center px-5 py-2.5 bg-slate-800 rounded-2xl border border-slate-700 font-bold text-base lg:text-lg shadow-lg">
                    {filteredResult.branchName}
                  </div>
                </div>

                <div className="mt-12 pt-10 border-t border-slate-800 flex flex-col gap-2">
                  <div className="flex items-center gap-3 text-slate-400">
                    <Calendar className="w-5 h-5 text-blue-500" />
                    <p className="text-sm font-bold">
                      Zbývá <strong className="text-white">{filteredResult.daysRemaining} dní</strong> v měsíci.
                    </p>
                  </div>
                  <p className="text-[10px] text-slate-500 font-bold uppercase tracking-widest mt-4">
                    Report: {new Date().toLocaleDateString('cs-CZ')}
                  </p>
                </div>
              </div>

              <div className="md:w-[60%] p-10 lg:p-16 space-y-6 lg:space-y-10 bg-white flex flex-col justify-center overflow-hidden">
                <div className="space-y-2">
                  <h3 className="text-3xl lg:text-4xl font-black text-slate-900 tracking-tight leading-none">Metriky</h3>
                  <p className="text-slate-400 font-medium text-sm lg:text-base">Načtené hodnoty pro výpočet</p>
                </div>

                <div className="space-y-3 lg:space-y-5">
                  <ValueRow label="Revenue RR" value={filteredResult.revenueRR} />
                  <ValueRow label="Plan ASR Services" value={filteredResult.planAsrServicesRevenue} isPercent truncate />
                  <ValueRow label="Service Asist" value={filteredResult.serviceAsistRevenue} isMinus />
                </div>

                <div className="mt-6 lg:mt-12 p-8 lg:p-10 bg-slate-50 rounded-[3rem] border border-slate-100 relative overflow-hidden group">
                  <div className="absolute top-0 right-0 p-4 opacity-5 group-hover:opacity-10 transition-opacity">
                    <Calculator className="w-16 lg:w-24 h-16 lg:h-24" />
                  </div>
                  <div className="flex items-center gap-3 mb-4 lg:mb-6 text-slate-400 relative z-10">
                    <Info className="w-5 h-5 text-blue-400" />
                    <span className="text-[10px] font-black uppercase tracking-[0.2em]">Logika výpočtu</span>
                  </div>
                  <div className="bg-white p-5 lg:p-6 rounded-2xl border border-slate-200 font-mono text-[10px] lg:text-[12px] text-slate-400 leading-relaxed shadow-sm relative z-10">
                    <span className="text-blue-500 font-bold">// ( (RevRR * PlanASR) - ServAsist ) / Dny</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

const ValueRow = ({ label, value, isPercent, isMinus, truncate }: any) => {
  let formattedVal: string;

  if (isPercent) {
    const percentVal = value * 100;
    if (truncate) {
      const truncated = Math.floor(percentVal * 100) / 100;
      formattedVal = truncated.toLocaleString('cs-CZ', { 
        minimumFractionDigits: 2, 
