  const resultsRef = useRef<HTMLDivElement>(null);

  // Automatické odscrollování k výsledkům po zpracování
  useEffect(() => {
    if (allCalculations.length > 0 && resultsRef.current && !isProcessing) {
      resultsRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }, [allCalculations, isProcessing]);

  const availableBranches = useMemo(() => {
    return Array.from(new Set(allCalculations.map(c => c.branchName))).sort();
  }, [allCalculations]);

  const filteredResult = useMemo(() => {
    if (!selectedBranch) return null;
    return allCalculations.find(c => c.branchName === selectedBranch) || null;
  }, [allCalculations, selectedBranch]);

  // Výpočet progressu pro zobrazení v UI
  const progressStats = useMemo(() => {
    if (!filteredResult) return { percent: 0, target: 0 };
    const target = filteredResult.revenueRR * filteredResult.planAsrServicesRevenue;
    const current = filteredResult.serviceAsistRevenue;
    const percent = target > 0 ? (current / target) * 100 : 0;
    return { percent, target };
  }, [filteredResult]);

  const processFile = async (file: File) => {
    setIsProcessing(true);
    setError(null);
    setFileName(file.name);
    setAllCalculations([]);
    setSelectedBranch("");

    try {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const ab = e.target?.result;
          const wb = XLSX.read(ab, { type: 'array' });

          const targetSheetName = "Branch Performance";
          let ws = wb.Sheets[targetSheetName];
          if (!ws) ws = wb.Sheets[wb.SheetNames[0]];

          const rows: any[][] = XLSX.utils.sheet_to_json(ws, { header: 1 });

          if (rows.length < 5) {
            throw new Error("Soubor je příliš krátký. Záhlaví musí být na 5. řádku.");
          }

          const headerRow = rows[4];
          const findColIndex = (names: string[]) => {
            return headerRow.findIndex(cell => 
              cell && names.some(name => String(cell).toLowerCase().trim() === name.toLowerCase().trim())
            );
          };

          const colIdxBranch = findColIndex(['BranchName', 'Pobočka']);
          const colIdxRevRR = findColIndex(['Revenue RR']);
          const colIdxServAsist = findColIndex(['Service Asist Revenue']);
          const colIdxPlanAsr = findColIndex(['Plan ASR Services/Revenue']);

          if (colIdxBranch === -1 || colIdxRevRR === -1 || colIdxServAsist === -1 || colIdxPlanAsr === -1) {
            setError("Nepodařilo se najít všechny sloupce. Zkontrolujte názvy v Excelu na 5. řádku.");
            setIsProcessing(false);
            return;
          }

          const daysInfo = getRemainingDaysInfo();
          const results: CalculationResult[] = [];

          for (let i = 5; i < rows.length; i++) {
            const row = rows[i];
            const branchVal = row[colIdxBranch]?.toString().trim();

            if (branchVal) {
              const parseVal = (val: any) => {
                if (typeof val === 'number') return val;
                if (!val) return 0;
                const cleaned = String(val).replace(/\s/g, '').replace(',', '.');
                const num = parseFloat(cleaned);
                return isNaN(num) ? 0 : num;
              };

              const revRR = Math.round(parseVal(row[colIdxRevRR]));
              const planAsr = parseVal(row[colIdxPlanAsr]);
              const serAsist = Math.round(parseVal(row[colIdxServAsist]));

              if (revRR === 0 && planAsr === 0) continue;

              const remainingRevenue = (revRR * planAsr) - serAsist;

              // Vážený výpočet: Cíl = R / (všední_dny + 0.6 * víkendy)
              // Weekday target (W)
              const weightedDays = daysInfo.weekdays + (0.6 * daysInfo.weekends);
              const weekdayTarget = remainingRevenue / weightedDays;

              // Aktuální cílová hodnota pro dnešek
              const finalValue = daysInfo.isTodayWeekend ? (weekdayTarget * 0.6) : weekdayTarget;

              results.push({
                branchName: branchVal,
                revenueRR: revRR,
                planAsrServicesRevenue: planAsr,
                serviceAsistRevenue: serAsist,
                daysRemaining: daysInfo.total,
                weekdaysRemaining: daysInfo.weekdays,
                weekendsRemaining: daysInfo.weekends,
                isTodayWeekend: daysInfo.isTodayWeekend,
                finalValue,
                rawRow: row
              });
            }
          }

          setAllCalculations(results);
          const budejovicka = results.find(r => r.branchName.toLowerCase().includes("budějovická"));
          setSelectedBranch(budejovicka ? budejovicka.branchName : (results[0]?.branchName || ""));

        } catch (err: any) {
          setError(err.message);
        } finally {
          setIsProcessing(false);
        }
      };
      reader.readAsArrayBuffer(file);
    } catch (err) {
      setError("Chyba při nahrávání.");
      setIsProcessing(false);
    }
  };

  const handleGeneratePDF = async () => {
    if (!resultsRef.current || !filteredResult) return;

    const element = resultsRef.current;
    const parent = element.parentElement;
    if (!parent) return;

    const opt = {
      margin: 0,
      filename: `Report_Služeb_${filteredResult.branchName.replace(/\s+/g, '_')}.pdf`,
      image: { type: 'jpeg', quality: 1.0 },
      html2canvas: { 
        scale: 3, 
        useCORS: true, 
        letterRendering: true,
        backgroundColor: '#ffffff',
        logging: false,
        y: 0,
        scrollX: 0,
        scrollY: 0,
        windowWidth: 1200
      },
      jsPDF: { unit: 'mm', format: 'a4', orientation: 'landscape' }
    };

    try {
      const originalElementStyle = element.style.cssText;
      const originalParentStyle = parent.style.cssText;

      parent.style.width = '297mm';
      parent.style.height = '210mm';
      parent.style.display = 'flex';
      parent.style.flexDirection = 'column';
      parent.style.alignItems = 'center';
      parent.style.justifyContent = 'center';
      parent.style.padding = '0';
      parent.style.backgroundColor = '#ffffff';
      parent.style.margin = '0';
      parent.style.position = 'relative';

      element.style.width = '275mm';
      element.style.height = '180mm';
      element.style.minHeight = '180mm';
      element.style.margin = '0';
      element.style.boxShadow = 'none';
      element.style.border = '1px solid #f1f5f9';
      element.style.borderRadius = '2.5rem';

      await window.html2pdf().set(opt).from(parent).save();

      element.style.cssText = originalElementStyle;
      parent.style.cssText = originalParentStyle;
    } catch (err) {
      console.error("Chyba při generování PDF:", err);
      alert("Chyba při generování PDF.");
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-8 md:py-12 font-sans text-slate-900">
      {/* PWA Install Info */}
      {showInstallInfo && (
        <div className="mb-8 bg-blue-50 border border-blue-100 rounded-3xl p-5 flex flex-col sm:flex-row items-center gap-4 animate-in slide-in-from-top duration-500" data-html2canvas-ignore="true">
          <div className="bg-blue-600 p-3 rounded-2xl text-white">
            <Download className="w-5 h-5" />
          </div>
          <div className="flex-grow text-center sm:text-left">
            <h4 className="font-black text-blue-900 text-sm">Chcete aplikaci na plochu?</h4>
            <p className="text-blue-600/70 text-xs font-medium">Klikněte na ikonu instalace v adresním řádku prohlížeče (vpravo nahoře).</p>
          </div>
          <button 
            onClick={() => setShowInstallInfo(false)}
            className="text-[10px] font-black uppercase tracking-widest text-blue-400 hover:text-blue-600 transition-colors px-4 py-2"
          >
            Zavřít
          </button>
        </div>
      )}

      <header className="mb-12 flex flex-col items-center" data-html2canvas-ignore="true">
        <div className="bg-blue-600 p-4 rounded-3xl shadow-xl mb-6">
          <Calculator className="w-10 h-10 text-white" />
        </div>
        <h1 className="text-3xl md:text-4xl font-black mb-3 text-center">
          Služby - <span className="text-blue-600">denní plánování</span>
        </h1>
      </header>

      <main className="space-y-10">
        <section data-html2canvas-ignore="true">
          <div className={`relative border-4 border-dashed rounded-[2.5rem] p-12 text-center transition-all duration-300
            ${fileName ? 'border-green-200 bg-green-50/30' : 'border-slate-200 bg-white hover:border-blue-300 shadow-sm hover:shadow-md'}`}>
            {!fileName && (
              <input
                type="file"
                accept=".xlsx, .xls"
                onChange={(e) => e.target.files?.[0] && processFile(e.target.files[0])}
                className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              />
            )}
            <div className="flex flex-col items-center gap-4">
              <div className={`p-4 rounded-full ${fileName ? 'bg-green-100 text-green-600' : 'bg-blue-50 text-blue-500'}`}>
                {fileName ? <CheckCircle2 className="w-8 h-8" /> : <FileText className="w-8 h-8" />}
              </div>
              <div>
                <p className="text-xl font-bold text-slate-800">{fileName || 'Vyber aktuální Denní Hlášení'}</p>
              </div>
              {fileName && (
                <button 
                  onClick={() => setFileName(null)} 
                  className="text-xs text-rose-500 font-black uppercase tracking-widest hover:text-rose-600 transition-colors relative z-20 mt-2"
                >
                  Nahrát jiný soubor
                </button>
              )}
            </div>
          </div>
        </section>

        {allCalculations.length > 0 && (
          <section className="max-w-3xl mx-auto" data-html2canvas-ignore="true">
            <div className="bg-white rounded-[2.5rem] p-6 shadow-xl border border-slate-100 flex flex-col sm:flex-row items-end gap-4">
              <div className="flex-grow w-full">
                <label className="block text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mb-3 ml-1">Zvolená pobočka</label>
                <div className="relative">
                  <select 
                    value={selectedBranch}
