                  <div className="mt-8 lg:mt-10 inline-flex flex-col items-start gap-4">
                    <div className="px-5 py-2.5 bg-slate-800 rounded-2xl border border-slate-700 font-bold text-base lg:text-lg shadow-lg">
                      {filteredResult.branchName}
                    </div>
                    {filteredResult.isTodayWeekend && (
                      <div className="flex items-center gap-2 text-amber-400 bg-amber-400/10 px-4 py-2 rounded-xl border border-amber-400/20">
                        <Info className="w-4 h-4" />
